// frontend/src/components/AssetDetails.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AssetDetails = ({ currentUser }) => {
  const { id } = useParams();
  const [asset, setAsset] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/assets/${id}`)
      .then(res => setAsset(res.data))
      .catch(err => console.error('Ошибка загрузки актива:', err));
  }, [id]);

  if (!asset) return <div>Загрузка...</div>;

  const handleChange = (e) => {
    setAsset({ ...asset, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5000/api/assets/${id}`, asset);
      setIsEditing(false);
      alert('Данные успешно сохранены');
    } catch (err) {
      alert('Ошибка при сохранении');
    }
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" gutterBottom>Карточка оборудования</Typography>
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={asset.serial_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
            />
            <TextField
              label="Инвентарный номер"
              name="inventory_number"
              value={asset.inventory_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
            />
            <TextField
              label="Наименование"
              name="name"
              value={asset.name || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
            />
          </Grid>

          <Grid item xs={6}>
            <TextField
              label="Местоположение"
              name="location"
              value={asset.location || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
            />
            <TextField
              label="Ответственный"
              name="responsible_person"
              value={asset.responsible_person || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
            />
            <TextField
              label="Фактический пользователь"
              name="actual_user"
              value={asset.actual_user || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
            />
          </Grid>
        </Grid>

        {/* Комментарии */}
        <TextField
          label="Комментарии"
          name="comments"
          value={asset.comments || ''}
          onChange={handleChange}
          fullWidth
          multiline
          rows={4}
          margin="normal"
          disabled={!isEditing}
        />

        {/* QR-код */}
        <Box mt={3} display="flex" justifyContent="center">
          <QRCodeSVG
            value={asset.inventory_number || 'IT-Актив'}
            size={200}
            bgColor="#ffffff"
            fgColor="#000000"
            level="H"
          />
        </Box>

        {/* Кнопки управления */}
        <Box mt={3} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={() => navigate('/')}>
            Назад к списку
          </Button>

          {currentUser?.role === 'admin' && (
            isEditing ? (
              <Button variant="contained" color="success" onClick={handleSave}>
                Сохранить
              </Button>
            ) : (
              <Button variant="contained" color="primary" onClick={() => setIsEditing(true)}>
                Редактировать
              </Button>
            )
          )}
        </Box>
      </Paper>
    </Container>
  );
};

export default AssetDetails;
