sourcesContent:
// frontend/src/components/AssetDetails.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Alert,
  CircularProgress,
  Grid,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormHelperText,
} from '@mui/material';
import axios from 'axios';
import { API_BASE_URL } from '../config'; // Импорт базового URL

const AssetDetails = ({ currentUser }) => { // Принимаем currentUser как пропс
  const { id } = useParams();
  const navigate = useNavigate();

  // Инициализация состояния актива со всеми возможными полями, включая динамические
  const [asset, setAsset] = useState({
    serial_number: '',
    inventory_number: '',
    type_id: '',
    brand: '',
    model: '',
    status_id: '',
    actual_user: '',
    responsible_person: '',
    department_id: '',
    room: '',
    purchase_date: '',
    comments: '',
    // Динамические поля
    diagonal: '',
    CPU: '',
    RAM: '',
    Drive: '',
    OS: '',
    IP_address: '',
    number: ''
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const [deleteError, setDeleteError] = useState('');
  const [qrCodeUrl, setQrCodeUrl] = useState(''); // Состояние для URL QR-кода

  // Состояния для редактирования
  const [isEditing, setIsEditing] = useState(false);
  const [editedAsset, setEditedAsset] = useState({});
  const [editErrors, setEditErrors] = useState({});

  // Состояния для выпадающих списков
  const [types, setTypes] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [employees, setEmployees] = useState([]);
  // dynamicFields - массив объектов {id, name, fieldName, value}
  const [dynamicFields, setDynamicFields] = useState([]);
  const [typeProperties, setTypeProperties] = useState([]);

  // Загрузка данных актива и справочников
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError('');
      try {
        // Параллельная загрузка данных
        const [assetRes, typesRes, statusesRes, departmentsRes, employeesRes] = await Promise.all([
          axios.get(`${API_BASE_URL}/api/assets/${id}`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/types`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/statuses`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/departments`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/employees`, { withCredentials: true }),
        ]);

        const assetData = assetRes.data;
        setAsset(assetData);
        // Инициализируем editedAsset теми же данными при загрузке
        setEditedAsset(assetData);
        setTypes(typesRes.data);
        setStatuses(statusesRes.data);
        setDepartments(departmentsRes.data);
        setEmployees(employeesRes.data);

        // Формируем URL для QR-кода (предполагается, что бэкенд предоставляет его по этому адресу)
        setQrCodeUrl(`${API_BASE_URL}/api/assets/${id}/qr`);

      } catch (err) {
        console.error('Ошибка при загрузке актива:', err);
        setError('Не удалось загрузить данные актива.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  // Загрузка свойств типа и инициализация dynamicFields при изменении asset.type_id или самого asset
  useEffect(() => {
    const fetchTypeProperties = async () => {
      // Если тип не выбран, очищаем свойства и поля
      if (!asset.type_id) {
        setTypeProperties([]);
        setDynamicFields([]);
        return;
      }

      try {
        const res = await axios.get(`${API_BASE_URL}/api/types/${asset.type_id}/properties`, { withCredentials: true });
        const properties = res.data;
        setTypeProperties(properties);

        // Инициализируем dynamicFields как массив объектов на основе полученных свойств и текущих значений asset
        const fields = properties.map(prop => {
          const fieldName = mapPropertyNameToAssetField(prop.name);
          return {
            id: prop.id,
            name: prop.name,
            fieldName: fieldName,
            value: asset[fieldName] || '' // Получаем значение из asset
          };
        });
        setDynamicFields(fields);
      } catch (err) {
        console.error('Ошибка при загрузке свойств типа:', err);
        setTypeProperties([]);
        setDynamicFields([]); // Устанавливаем пустой массив
      }
    };

    // Зависимости: asset.type_id и asset, чтобы значения брались из актуального состояния
    fetchTypeProperties();
  }, [asset.type_id, asset]);

  // Функция маппинга названий свойств на поля актива
  // Убедитесь, что эта функция идентична той, что используется в AddAsset.js
  const mapPropertyNameToAssetField = (propertyName) => {
    const mapping = {
      'Диагональ': 'diagonal',
      'Диагональ (дюймы)': 'diagonal',
      'Операционная система': 'OS',
      'ОС': 'OS',
      'Процессор': 'CPU',
      'Оперативная память (ГБ)': 'RAM',
      'Оперативная память': 'RAM',
      'Диск (HDD/SSD)': 'Drive',
      'Диск': 'Drive',
      'IP-адрес': 'IP_address',
      'Внутренний номер': 'number',
    };
    // Если имя не найдено в маппинге, преобразуем его в snake_case
    return mapping[propertyName] || propertyName.toLowerCase().replace(/\s+/g, '_');
  };


  // Обработчик изменения динамических полей
  const handleDynamicFieldChange = (fieldName, value) => {
    if (isEditing) {
      // В режиме редактирования обновляем editedAsset
      setEditedAsset(prev => ({ ...prev, [fieldName]: value }));
      
      // Обновляем состояние dynamicFields (массив объектов)
      setDynamicFields(prevFields =>
        prevFields.map(field =>
          field.fieldName === fieldName ? { ...field, value } : field
        )
      );
    } else {
       // В режиме просмотра обновляем asset
       setAsset(prev => ({ ...prev, [fieldName]: value }));
      
       // Обновляем состояние dynamicFields (массив объектов)
       setDynamicFields(prevFields =>
         prevFields.map(field =>
           field.fieldName === fieldName ? { ...field, value } : field
         )
       );
     }
  };

  // Обработчик изменения типа устройства
  const handleTypeChange = async (e) => {
    const newTypeId = parseInt(e.target.value, 10) || '';
    
    if (isEditing) {
        // В режиме редактирования обновляем editedAsset
        setEditedAsset(prev => ({ ...prev, type_id: newTypeId }));
    } else {
        // В режиме просмотра обновляем asset
        setAsset(prev => ({ ...prev, type_id: newTypeId }));
    }

    // Загрузка свойств нового типа и обновление dynamicFields
    if (newTypeId) {
      try {
        const propsRes = await axios.get(`${API_BASE_URL}/api/types/${newTypeId}/properties`, { withCredentials: true });
        setTypeProperties(propsRes.data);

        // Инициализируем dynamicFields как МАССИВ ОБЪЕКТОВ для нового типа
        const initialDynamicFields = propsRes.data.map(property => {
          const fieldName = mapPropertyNameToAssetField(property.name);
          // Используем текущее значение из editedAsset/asset или пустую строку
          const currentValue = isEditing ? editedAsset[fieldName] : asset[fieldName];
          return {
            id: property.id,
            name: property.name,
            fieldName: fieldName,
            value: currentValue || ''
          };
        });
        setDynamicFields(initialDynamicFields); // Устанавливаем массив объектов
      } catch (err) {
        console.error('Ошибка при загрузке свойств нового типа:', err);
        setTypeProperties([]);
        setDynamicFields([]); // Устанавливаем пустой массив
        alert('Не удалось загрузить свойства для выбранного типа.');
      }
    } else {
      // Если тип не выбран, очищаем свойства и поля
      setTypeProperties([]);
      setDynamicFields([]); // Устанавливаем пустой массив
    }
  };

  // Общий обработчик изменения для стандартных полей ввода
  const handleChange = (e) => {
    const { name, value } = e.target;
    if (isEditing) {
      // В режиме редактирования обновляем editedAsset
      setEditedAsset(prev => ({ ...prev, [name]: value }));
    } else {
      // В режиме просмотра обновляем asset
      setAsset(prev => ({ ...prev, [name]: value }));
    }
  };

  // Обработчик сохранения изменений
  const handleSave = async () => {
    try {
      // Определяем, из какого состояния брать данные для сохранения
      const dataToUse = isEditing ? editedAsset : asset;

      // Формируем объект данных для отправки
      const dataToSend = {
        serial_number: dataToUse.serial_number,
        inventory_number: dataToUse.inventory_number,
        type_id: dataToUse.type_id,
        brand: dataToUse.brand,
        model: dataToUse.model,
        status_id: dataToUse.status_id,
        actual_user: dataToUse.actual_user,
        responsible_person: dataToUse.responsible_person,
        department_id: dataToUse.department_id,
        room: dataToUse.room,
        purchase_date: dataToUse.purchase_date,
        comments: dataToUse.comments,
        // Добавляем ВСЕ текущие значения динамических полей из массива dynamicFields
        ...dynamicFields.reduce((acc, field) => {
          acc[field.fieldName] = field.value;
          return acc;
        }, {})
      };

      const response = await axios.put(
        `${API_BASE_URL}/api/assets/${id}`,
        dataToSend,
        { withCredentials: true } // ВАЖНО для аутентификации
      );

      // После успешного сохранения обновляем основное состояние asset
      setAsset(response.data); // Предполагаем, что сервер возвращает обновленный объект
      alert('Данные успешно обновлены');
      setIsEditing(false); // Выходим из режима редактирования

    } catch (err) {
      console.error('Ошибка при сохранении изменений:', err);
      let errorMessage = 'Неизвестная ошибка';
      if (err.response) {
        if (err.response.data && err.response.data.error) {
          errorMessage = err.response.data.error;
          if (err.response.data.details) {
            errorMessage += ` (${err.response.data.details})`;
          }
        } else if (err.response.data && err.response.data.message) {
          errorMessage = err.response.data.message;
        } else {
          errorMessage = `Ошибка ${err.response.status}: ${err.response.statusText}`;
        }
        // Специальная обработка ошибки нарушения внешнего ключа
        if (err.response.status === 500 && err.response.data && err.response.data.error && err.response.data.error.includes('foreign key constraint')) {
            errorMessage = 'Невозможно удалить актив, так как на него ссылаются записи в истории изменений.';
        }
      } else if (err.request) {
        errorMessage = 'Нет ответа от сервера';
      } else {
        errorMessage = err.message;
      }
      alert(`Ошибка при сохранении изменений: ${errorMessage}`);
    }
  };

  // Обработчик удаления актива
  const handleDelete = async () => {
    try {
      await axios.delete(`${API_BASE_URL}/api/assets/${id}`, { withCredentials: true });
      alert('Актив успешно удален');
      navigate('/assets'); // Перенаправляем на список активов
    } catch (err) {
      console.error('Ошибка при удалении актива:', err);
      let errorMessage = 'Не удалось удалить актив.';
      // Специальная обработка ошибки нарушения внешнего ключа
      if (err.response && err.response.status === 500 && err.response.data && err.response.data.error && err.response.data.error.includes('foreign key constraint')) {
          errorMessage = 'Невозможно удалить актив, так как на него ссылаются записи в истории изменений.';
      } else if (err.response) {
          errorMessage += ` Ошибка ${err.response.status}.`;
      }
      setDeleteError(errorMessage); // Устанавливаем сообщение об ошибке для отображения в диалоге
      // alert(errorMessage); // Можно также показать alert, но диалог предпочтительнее
    }
  };

  const handleOpenDeleteDialog = () => {
    setOpenDeleteDialog(true);
  };

  const handleCloseDeleteDialog = () => {
    setOpenDeleteDialog(false);
    setDeleteError(''); // Очищаем ошибку при закрытии диалога
  };

  // Функция рендеринга динамического поля
  const renderDynamicField = (field) => {
    // Определяем, из какого состояния брать значение для отображения
    const valueToUse = isEditing ? (editedAsset[field.fieldName] || '') : (asset[field.fieldName] || '');
    // Получаем актуальное значение из dynamicFields для редактирования
    const dynamicFieldValue = dynamicFields.find(f => f.fieldName === field.fieldName)?.value || valueToUse;

    return (
      <Grid item xs={12} sm={6} key={field.id}>
        <TextField
          label={field.name}
          name={field.fieldName}
          value={dynamicFieldValue}
          onChange={(e) => handleDynamicFieldChange(field.fieldName, e.target.value)}
          fullWidth
          margin="normal"
          disabled={!isEditing || !currentUser || currentUser.role !== 'admin'}
        />
      </Grid>
    );
  };

  // Функция печати QR-кода
  const handlePrintQRCode = async () => {
    if (!asset.inventory_number) {
      alert('Инвентарный номер не указан');
      return;
    }

    try {
      // Получаем QR-код с сервера (предполагается, что сервер возвращает base64)
      const res = await axios.post(`${API_BASE_URL}/api/qrcodes`, {
        ids: [asset.id], // Отправляем ID текущего актива
      }, { withCredentials: true });

      const codes = res.data;

      // Генерируем HTML-контент для печати
      let htmlContent = `
        <html>
          <head>
            <style>
              @page {
                size: A4;
                margin: 0;
              }
              body {
                width: 21cm;
                padding: 0;
                background-color: #fff;
                font-family: Arial, sans-serif;
              }
              .page {
                display: flex;
                flex-wrap: wrap;
                gap: 10px;
                padding: 20px;
                box-sizing: border-box;
                page-break-after: always;
                break-after: page;
              }
              .qr-wrapper {
                width: 3cm; /* Ширина контейнера */
                height: 3cm; /* Высота контейнера */
                position: relative;
                text-align: center;
                font-size: 10px;
                box-sizing: border-box;
                page-break-inside: avoid;
                break-inside: avoid;
              }
              .qr-container {
                width: 100%;
                height: 100%;
                display: flex;
                justify-content: center;
                align-items: center;
              }
              img {
                max-width: 100%;
                max-height: 100%;
                object-fit: contain;
              }
              .text-top, .text-bottom {
                position: absolute;
                left: 50%; /* Центрирование по горизонтали */
                transform: translateX(-50%); /* Корректировка центрирования */
                white-space: nowrap; /* Запрет переноса строки */
                overflow: hidden; /* Скрывает лишний текст */
                text-overflow: ellipsis; /* Добавляет многоточие при обрезке */
                width: 90%; /* Ограничиваем ширину текста */
                font-size: 8px; /* Маленький размер шрифта */
              }
              .text-top {
                top: 0;
              }
              .text-bottom {
                bottom: 0;
              }
            </style>
          </head>
          <body>
      `;

      codes.forEach((code) => {
        // Используем данные, полученные с сервера
        htmlContent += `
          <div class="qr-wrapper">
            <div class="qr-container">
              <img src="data:image/png;base64,${code.qr_base64}" />
            </div>
            <span class="text-top">${code.full_name || ''}</span>
            <span class="text-bottom">${code.inventory_number || ''}</span>
          </div>
        `;
      });

      htmlContent += '</body></html>';

      // Создаем новое окно для печати
      const printWindow = window.open('', '_blank');
      printWindow.document.write(htmlContent);

      // Небольшая задержка перед печатью, чтобы контент успел загрузиться
      setTimeout(() => {
        printWindow.print();
        printWindow.close();
      }, 500);
    } catch (err) {
      console.error('Ошибка при генерации QR-кода:', err);
      alert('Не удалось получить QR-код с сервера');
    }
  };

  // Условный рендеринг состояний загрузки/ошибки/отсутствия данных
  if (loading) return (
    <Container maxWidth="md" style={{ marginTop: '20px', textAlign: 'center' }}>
      <CircularProgress />
      <Typography>Загрузка...</Typography>
    </Container>
  );
  if (error) return (
    <Container maxWidth="md" style={{ marginTop: '20px' }}>
      <Alert severity="error">{error}</Alert>
    </Container>
  );
  if (!asset) return (
    <Container maxWidth="md" style={{ marginTop: '20px' }}>
      <Alert severity="warning">Актив не найден</Alert>
    </Container>
  );

  // Определяем, из какого состояния брать данные для отображения
  const dataToDisplay = isEditing ? editedAsset : asset;

  return (
    <Container maxWidth="md">
      <Box component="form" sx={{ display: 'flex', flexDirection: 'column', gap: 1.5 }}>
        {/* Шапка с полным названием */}
        <Typography variant="h5" gutterBottom>
          {(types.find(t => t.id === dataToDisplay.type_id)?.name || 'Без типа')} {dataToDisplay.brand} {dataToDisplay.model}
        </Typography>

        <Grid container spacing={2}>
          {/* Пара 1: Серийный номер | Инвентарный номер */}
          <Grid item xs={12} sm={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={dataToDisplay.serial_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              sx={{ mb: -1 }}
              disabled={!isEditing || !currentUser || currentUser.role !== 'admin'}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              label="Инвентарный номер *"
              name="inventory_number"
              value={dataToDisplay.inventory_number}
              onChange={handleChange}
              fullWidth
              margin="normal"
              sx={{ mb: -1 }}
              error={!!(isEditing ? editErrors.inventory_number : null)}
              helperText={isEditing ? editErrors.inventory_number : null}
              disabled={!isEditing || !currentUser || currentUser.role !== 'admin'}
            />
          </Grid>

          {/* Пара 2: Тип устройства | Фирма */}
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth margin="normal" error={!!(isEditing ? editErrors.type_id : null)}>
              <InputLabel id="edit-type-label">Тип устройства *</InputLabel>
              <Select
                labelId="edit-type-label"
                label="Тип устройства *"
                name="type_id"
                value={dataToDisplay.type_id}
                onChange={handleTypeChange}
                sx={{ mb: -2 }}
                disabled={!isEditing || !currentUser || currentUser.role !== 'admin'}
              >
                {types.map(type => (
                  <MenuItem key={type.id} value={type.id}>
                    {type.name}
                  </MenuItem>
                ))}
              </Select>
              {isEditing && editErrors.type_id && (
                <FormHelperText>{editErrors.type_id}</FormHelperText>
              )}
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              label="Фирма"
              name="brand"
              value={dataToDisplay.brand}
              onChange={handleChange}
              fullWidth
              margin="normal"
              sx={{ mb: -2 }}
              disabled={!isEditing || !currentUser || currentUser.role !== 'admin'}
            />
          </Grid>

          {/* Пара 3: Модель | Динамические поля (первая часть) */}
          <Grid item xs={12} sm={6}>
            <TextField
              label="Модель"
              name="model"
              value={dataToDisplay.model}
              onChange={handleChange}
              fullWidth
              margin="normal"
              sx={{ mb: -2 }}
              disabled={!isEditing || !currentUser || currentUser.role !== 'admin'}
            />
          </Grid>
          {/* Первое динамическое поле, если оно есть */}
          {typeProperties.length > 0 && dynamicFields.length > 0 && renderDynamicField(dynamicFields[0])}

          {/* Статус | Фактический пользователь */}
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth margin="normal" error={!!(isEditing ? editErrors.status_id : null)}>
              <InputLabel id="edit-status-label">Статус *</InputLabel>
              <Select
                labelId="edit-status-label"
                label="Статус *"
                name="status_id"
                value={dataToDisplay.status_id}
                onChange={handleChange}
                sx={{ mb: -2 }}
                disabled={!isEditing || !currentUser || currentUser.role !== 'admin'}
              >
                {statuses.map(stat => (
                  <MenuItem key={stat.id} value={stat.id}>
                    {stat.name}
                  </MenuItem>
                ))}
              </Select>
              {isEditing && editErrors.status_id && (
                <FormHelperText>{editErrors.status_id}</FormHelperText>
              )}
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              label="Фактический пользователь"
              name="actual_user"
              value={dataToDisplay.actual_user}
              onChange={handleChange}
              fullWidth
              margin="normal"
              sx={{ mb: -2 }}
              disabled={!isEditing || !currentUser || currentUser.role !== 'admin'}
            />
          </Grid>

          {/* Ответственный | Отдел (подразделение) - ВСЕГДА СЕРЫЙ и ОТКЛЮЧЕННЫЙ */}
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth margin="normal" error={!!(isEditing ? editErrors.responsible_person : null)}>
              <InputLabel id="edit-responsible-person-label">Ответственный *</InputLabel>
              <Select
                labelId="edit-responsible-person-label"
                label="Ответственный *"
                name="responsible_person"
                value={dataToDisplay.responsible_person}
                onChange={handleChange}
                sx={{ mb: -2 }}
                disabled={!isEditing || !currentUser || currentUser.role !== 'admin'}
              >
                {employees.map(emp => (
                  <MenuItem key={emp.id} value={emp.id}>
                    {emp.name}
                  </MenuItem>
                ))}
              </Select>
              {isEditing && editErrors.responsible_person && (
                <FormHelperText>{editErrors.responsible_person}</FormHelperText>
              )}
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth margin="normal">
              <InputLabel id="edit-department-label">Отдел (подразделение)</InputLabel>
              <Select
                labelId="edit-department-label"
                label="Отдел (подразделение)"
                name="department_id"
                value={dataToDisplay.department_id || ''}
                onChange={handleChange}
                // ВАЖНО: Всегда disabled и всегда с серым стилем
                disabled
                style={{
                  backgroundColor: '#f5f5f5', // Серый фон
                  color: 'rgba(0, 0, 0, 0.87)', // Цвет текста
                  cursor: 'not-allowed' // Курсор "запрещено"
                }}
                sx={{
                  mb: -2,
                  '& .MuiOutlinedInput-notchedOutline': { borderColor: 'rgba(0, 0, 0, 0.23)' },
                  '& .MuiSelect-select': { color: 'rgba(0, 0, 0, 0.87)' }
                }}
              >
                {departments.map(dept => (
                  <MenuItem key={dept.id} value={dept.id}>
                    {dept.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          {/* Помещение | Дата покупки */}
          <Grid item xs={12} sm={6}>
            <TextField
              label="Помещение"
              name="room"
              value={dataToDisplay.room}
              onChange={handleChange}
              fullWidth
              margin="normal"
              sx={{ mb: -2 }}
              disabled={!isEditing || !currentUser || currentUser.role !== 'admin'}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              label="Дата покупки *"
              name="purchase_date"
              type="date"
              value={dataToDisplay.purchase_date}
              onChange={handleChange}
              fullWidth
              margin="normal"
              sx={{ mb: -2 }}
              error={!!(isEditing ? editErrors.purchase_date : null)}
              helperText={isEditing ? editErrors.purchase_date : null}
              InputLabelProps={{
                shrink: true,
              }}
              disabled={!isEditing || !currentUser || currentUser.role !== 'admin'}
            />
          </Grid>

          {/* Остальные динамические поля */}
          {dynamicFields.slice(1).map(renderDynamicField)}

          {/* Комментарий - растянут на всю ширину */}
          <Grid item xs={12}>
            <TextField
              label="Комментарий"
              name="comments"
              value={dataToDisplay.comments}
              onChange={handleChange}
              fullWidth
              multiline
              rows={3}
              margin="normal"
              sx={{ mb: -2 }}
              disabled={!isEditing || !currentUser || currentUser.role !== 'admin'}
            />
          </Grid>

          {/* <<<--- ИЗМЕНЕНО: QR-код в той же карточке, без заголовка */}
          <Grid item xs={12} display="flex" justifyContent="center" mt={2}>
            {qrCodeUrl ? (
              <img
                src={qrCodeUrl}
                alt={`QR-код для актива ${dataToDisplay.inventory_number}`}
                style={{ maxWidth: '200px', height: 'auto' }}
              />
            ) : (
              <Typography>Загрузка QR-кода...</Typography>
            )}
          </Grid>
        </Grid>
      </Box>

      <Box mt={0.5} display="flex" justifyContent="center">
        <Button variant="contained" color="primary" onClick={handlePrintQRCode}>
          Печать QR
        </Button>
      </Box>

      <Box mt={4.5} display="flex" justifyContent="space-between" alignItems="center">
        <Button onClick={() => navigate('/assets')} variant="outlined">
          Назад
        </Button>

        <Box>
          {currentUser && currentUser.role === 'admin' && (
            <>
              {isEditing ? (
                <>
                  <Button
                    variant="outlined"
                    color="secondary"
                    onClick={() => setIsEditing(false)}
                    sx={{ mr: '8px' }}
                  >
                    Отмена
                  </Button>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={handleSave}
                  >
                    Сохранить
                  </Button>
                </>
              ) : (
                <>
                  <Button
                    variant="contained"
                    color="error"
                    onClick={handleOpenDeleteDialog}
                    sx={{ mr: '16px' }}
                  >
                    Удалить
                  </Button>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={() => setIsEditing(true)}
                  >
                    Редактировать
                  </Button>
                </>
              )}
            </>
          )}
        </Box>
      </Box>

      {/* Диалог подтверждения удаления */}
      <Dialog
        open={openDeleteDialog}
        onClose={handleCloseDeleteDialog}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{"Подтвердите удаление"}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Вы уверены, что хотите удалить актив "{dataToDisplay.inventory_number}"?
            Это действие нельзя отменить.
          </DialogContentText>
          {deleteError && (
            <Alert severity="error" style={{ marginTop: '10px' }}>
              {deleteError}
            </Alert>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDeleteDialog} color="primary">
            Отмена
          </Button>
          <Button onClick={handleDelete} color="error" autoFocus>
            Удалить
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default AssetDetails;
