// frontend/src/components/AssetDetails.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Alert,
  CircularProgress,
  Grid,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormHelperText,
} from '@mui/material';
import axios from 'axios';
import { API_BASE_URL } from '../config'; // Импорт базового URL

const AssetDetails = ({ currentUser }) => { // Принимаем currentUser как пропс
  const { id } = useParams();
  const navigate = useNavigate();
  const [asset, setAsset] = useState({
    serial_number: '',
    inventory_number: '',
    type_id: '',
    brand: '',
    model: '',
    status_id: '',
    actual_user: '',
    responsible_person: '',
    department_id: '',
    room: '',
    purchase_date: '',
    comments: '',
    diagonal: '',
    CPU: '',
    RAM: '',
    Drive: '',
    OS: '',
    IP_address: '',
    number: ''
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const [deleteError, setDeleteError] = useState('');
  const [qrCodeUrl, setQrCodeUrl] = useState(''); // Состояние для URL QR-кода

  // Состояния для редактирования
  const [isEditing, setIsEditing] = useState(false);
  const [editedAsset, setEditedAsset] = useState({});
  const [editErrors, setEditErrors] = useState({});

  // Состояния для выпадающих списков
  const [types, setTypes] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [employees, setEmployees] = useState([]);
  // Исправлено: dynamicFields должен быть массивом
  const [dynamicFields, setDynamicFields] = useState([]);
  const [typeProperties, setTypeProperties] = useState([]);

  // Загрузка данных актива и справочников
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError('');
      try {
        const [assetRes, typesRes, statusesRes, departmentsRes, employeesRes] = await Promise.all([
          axios.get(`${API_BASE_URL}/api/assets/${id}`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/types`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/statuses`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/departments`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/employees`, { withCredentials: true }),
        ]);

        setAsset(assetRes.data);
        setEditedAsset(assetRes.data); // Инициализируем данные для редактирования
        setTypes(typesRes.data);
        setStatuses(statusesRes.data);
        setDepartments(departmentsRes.data);
        setEmployees(employeesRes.data);

        // Формируем URL для QR-кода
        setQrCodeUrl(`${API_BASE_URL}/api/assets/${id}/qr`);

      } catch (err) {
        console.error('Ошибка при загрузке актива:', err);
        setError('Не удалось загрузить данные актива.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  // Исправлено: добавлен asset в зависимости и исправлена установка dynamicFields
  useEffect(() => {
    const fetchTypeProperties = async () => {
      if (!asset.type_id) {
        setTypeProperties([]);
        // Исправлено: устанавливаем пустой массив
        setDynamicFields([]);
        return;
      }

      try {
        const res = await axios.get(`${API_BASE_URL}/api/types/${asset.type_id}/properties`, { withCredentials: true });
        setTypeProperties(res.data);

        const fields = res.data.map(prop => {
          const fieldName = mapPropertyNameToAssetField(prop.name);
          // Используем editedAsset или asset, в зависимости от того, какие данные актуальны в этот момент
          // Если editedAsset всегда актуален, используйте его. Иначе добавьте asset в зависимости.
          return {
            id: prop.id,
            name: prop.name,
            fieldName: fieldName,
            value: asset[fieldName] || '' // Или editedAsset[fieldName] || ''
          };
        });
        setDynamicFields(fields);
      } catch (err) {
        console.error('Ошибка при загрузке свойств типа:', err);
        setTypeProperties([]);
        // Исправлено: устанавливаем пустой массив
        setDynamicFields([]);
      }
    };

    // Исправлено: добавлен asset в зависимости
    fetchTypeProperties();
  }, [asset.type_id, asset]); // Добавлен asset в зависимости

  // Функция маппинга названий свойств на поля актива
  const mapPropertyNameToAssetField = (propertyName) => {
    const mapping = {
      'Диагональ': 'diagonal',
      'Диагональ (дюймы)': 'diagonal',
      'Операционная система': 'OS',
      'ОС': 'OS',
      'Процессор': 'CPU',
      'Оперативная память (ГБ)': 'RAM',
      'Оперативная память': 'RAM',
      'Диск (HDD/SSD)': 'Drive',
      'Диск': 'Drive',
      'IP-адрес': 'IP_address', // В JS удобнее работать с IP_address
      'Внутренний номер': 'number',
    };
    return mapping[propertyName] || propertyName.toLowerCase().replace(/\s+/g, '_');
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`${API_BASE_URL}/api/assets/${id}`, { withCredentials: true });
      alert('Актив успешно удален');
      navigate('/assets');
    } catch (err) {
      console.error('Ошибка при удалении актива:', err);
      setDeleteError('Не удалось удалить актив.');
    }
  };

  const handleOpenDeleteDialog = () => {
    setOpenDeleteDialog(true);
  };

  const handleCloseDeleteDialog = () => {
    setOpenDeleteDialog(false);
    setDeleteError('');
  };

  const handlePrintQRCode = async () => {
    if (!asset.inventory_number) {
      alert('Инвентарный номер не указан');
      return;
    }

    try {
      // Получаем QR-код с сервера
      const res = await axios.post(`${API_BASE_URL}/api/qrcodes`, {
        ids: [asset.id],
      }, { withCredentials: true });

      const codes = res.data;

      // Генерируем HTML-контент для печати
      let htmlContent = `
        <html>
          <head>
            <style>
              @page {
                size: A4;
                margin: 0;
              }
              body {
                width: 21cm;
                padding: 0;
                background-color: #fff;
                font-family: Arial, sans-serif;
              }
              .page {
                display: flex;
                flex-wrap: wrap;
                gap: 10px;
                padding: 20px;
                box-sizing: border-box;
                page-break-after: always;
                break-after: page;
              }
              .qr-wrapper {
                width: 3cm; /* Ширина контейнера */
                height: 3cm; /* Высота контейнера */
                position: relative;
                text-align: center;
                font-size: 10px;
                box-sizing: border-box;
                page-break-inside: avoid;
                break-inside: avoid;
              }
              .qr-container {
                width: 100%;
                height: 100%;
                display: flex;
                justify-content: center;
                align-items: center;
              }
              img {
                max-width: 100%;
                max-height: 100%;
                object-fit: contain;
              }
              .text-top, .text-bottom {
                position: absolute;
                left: 50%; /* Центрирование по горизонтали */
                transform: translateX(-50%); /* Корректировка центрирования */
                white-space: nowrap; /* Запрет переноса строки */
                overflow: hidden; /* Скрывает лишний текст */
                text-overflow: ellipsis; /* Добавляет многоточие при обрезке */
                width: 90%; /* Ограничиваем ширину текста */
                font-size: 8px; /* Маленький размер шрифта */
              }
              .text-top {
                top: 0;
              }
              .text-bottom {
                bottom: 0;
              }
            </style>
          </head>
          <body>
      `;

      codes.forEach((code) => {
        htmlContent += `
          <div class="qr-wrapper">
            <div class="qr-container">
              <img src="data:image/png;base64,${code.qr_base64}" />
            </div>
            <span class="text-top">${code.full_name}</span>
            <span class="text-bottom">${code.inventory_number}</span>
          </div>
        `;
      });

      htmlContent += '</body></html>';

      // Создаем новое окно для печати
      const printWindow = window.open('', '_blank');
      printWindow.document.write(htmlContent);

      setTimeout(() => {
        printWindow.print();
        printWindow.close();
      }, 500);
    } catch (err) {
      console.error('Ошибка при генерации QR-кода:', err);
      alert('Не удалось получить QR-код с сервера');
    }
  };








  // Обработчик изменения динамических полей
  // Предполагается, что dynamicFields - это массив объектов {id, name, fieldName, value}
  const handleDynamicFieldChange = (fieldName, value) => {
    // Обновляем основное состояние asset
    setAsset(prev => ({ ...prev, [fieldName]: value }));
    
    // Обновляем состояние dynamicFields (массив объектов)
    setDynamicFields(prevFields =>
      prevFields.map(field =>
        field.fieldName === fieldName ? { ...field, value } : field
      )
    );
  };

  // Обработчик изменения типа устройства
  const handleTypeChange = async (e) => {
    const newTypeId = parseInt(e.target.value, 10) || '';
    handleChange(e); // Обновляем asset.type_id через общий обработчик

    if (newTypeId) {
      try {
        // Загружаем свойства нового типа
        const propsRes = await axios.get(`${API_BASE_URL}/api/types/${newTypeId}/properties`, { withCredentials: true });
        setTypeProperties(propsRes.data);

        // Инициализируем dynamicFields как МАССИВ ОБЪЕКТОВ для нового типа
        const initialDynamicFields = propsRes.data.map(property => {
          const fieldName = mapPropertyNameToAssetField(property.name);
          // Используем текущее значение из asset (или editedAsset, если оно там актуальнее) или пустую строку
          return {
            id: property.id,
            name: property.name,
            fieldName: fieldName,
            value: asset[fieldName] || '' 
          };
        });
        setDynamicFields(initialDynamicFields); // Устанавливаем массив объектов
      } catch (err) {
        console.error('Ошибка при загрузке свойств нового типа:', err);
        setTypeProperties([]);
        setDynamicFields([]); // Устанавливаем пустой массив, а не объект
        alert('Не удалось загрузить свойства для выбранного типа.');
      }
    } else {
      // Если тип не выбран, очищаем свойства и поля
      setTypeProperties([]);
      setDynamicFields([]); // Устанавливаем пустой массив, а не объект
    }
  };

  // Общий обработчик изменения для стандартных полей ввода
  const handleChange = (e) => {
    const { name, value } = e.target;
    setAsset({ ...asset, [name]: value });
  };

  // Обработчик сохранения изменений
  const handleSave = async () => {
    try {
      // Формируем объект данных для отправки
      // Начинаем с данных из editedAsset (если используется режим редактирования) или asset
      // Предполагая, что editedAsset содержит актуальные изменения
      const dataToSend = {
        serial_number: editedAsset.serial_number,
        inventory_number: editedAsset.inventory_number,
        type_id: editedAsset.type_id,
        brand: editedAsset.brand,
        model: editedAsset.model,
        status_id: editedAsset.status_id,
        actual_user: editedAsset.actual_user,
        responsible_person: editedAsset.responsible_person,
        department_id: editedAsset.department_id,
        room: editedAsset.room,
        purchase_date: editedAsset.purchase_date || asset.purchase_date, // Сохраняем старую дату, если новая пустая
        comments: editedAsset.comments,
        // Добавляем ВСЕ текущие значения динамических полей из массива dynamicFields
        // Это гарантирует, что неизмененные поля не потеряются
        ...dynamicFields.reduce((acc, field) => {
          acc[field.fieldName] = field.value;
          return acc;
        }, {})
      };

      const response = await axios.put(
        `${API_BASE_URL}/api/assets/${id}`,
        dataToSend,
        { withCredentials: true } // ВАЖНО для аутентификации
      );

      alert('Данные успешно обновлены');
      setIsEditing(false); // Выходим из режима редактирования (если используется)

    } catch (err) {
      console.error('Ошибка при сохранении изменений:', err);
      let errorMessage = 'Неизвестная ошибка';
      if (err.response) {
        if (err.response.data && err.response.data.error) {
          errorMessage = err.response.data.error;
          if (err.response.data.details) {
            errorMessage += ` (${err.response.data.details})`;
          }
        } else if (err.response.data && err.response.data.message) {
          errorMessage = err.response.data.message;
        } else {
          errorMessage = `Ошибка ${err.response.status}: ${err.response.statusText}`;
        }
      } else if (err.request) {
        errorMessage = 'Нет ответа от сервера';
      } else {
        errorMessage = err.message;
      }
      alert(`Ошибка при сохранении изменений: ${errorMessage}`);
    }
  };

  // Функция рендеринга динамического поля
  // Предполагается, что field - это объект {id, name, fieldName, value}
  const renderDynamicField = (field) => {
    return (
      <Grid item xs={12} sm={6} key={field.id}>
        <TextField
          label={field.name}
          name={field.fieldName}
          value={field.value}
          onChange={(e) => handleDynamicFieldChange(field.fieldName, e.target.value)}
          fullWidth
          margin="normal"
          sx={{ mb: -2 }}
          disabled={!currentUser || currentUser.role !== 'admin'}
        />
      </Grid>
    );
  };

  // Условный рендеринг состояний загрузки/ошибки/отсутствия данных
  if (loading) return (
    <Container maxWidth="md" style={{ marginTop: '20px', textAlign: 'center' }}>
      <CircularProgress />
      <Typography>Загрузка...</Typography>
    </Container>
  );
  if (error) return (
    <Container maxWidth="md" style={{ marginTop: '20px' }}>
      <Alert severity="error">{error}</Alert>
    </Container>
  );
  if (!asset) return (
    <Container maxWidth="md" style={{ marginTop: '20px' }}>
      <Alert severity="warning">Актив не найден</Alert>
    </Container>
  );




  


  return (
    <Container maxWidth="md">
      <Box component="form" sx={{ display: 'flex', flexDirection: 'column', gap: 1.5 }}>
        {/* Заголовок карточки актива */}
        <Typography variant="h5" gutterBottom>
          {/* Отображаем составное имя актива, используя данные из asset */}
          {types.find(t => t.id === asset.type_id)?.name || 'Без типа'} {asset.brand} {asset.model}
        </Typography>

        {/* Сетка для полей ввода */}
        <Grid container spacing={2}>
          {/* === Пара 1: Серийный номер | Инвентарный номер === */}
          <Grid item xs={12} sm={6}>
            {/* Серийный номер */}
            <TextField
              label="Серийный номер"
              name="serial_number"
              // Используем значение из editedAsset в режиме редактирования, иначе из asset
              value={isEditing ? (editedAsset.serial_number || '') : (asset.serial_number || '')}
              onChange={handleChange}
              fullWidth
              // Используем margin="dense" вместо margin="normal" и отрицательных sx отступов
              margin="dense"
              // Отключаем поле, если пользователь не администратор
              disabled={!currentUser || currentUser.role !== 'admin'}
            />
          </Grid>

          <Grid item xs={12} sm={6}>
            {/* Инвентарный номер (обязательное поле) */}
            <TextField
              label="Инвентарный номер *"
              name="inventory_number"
              value={isEditing ? (editedAsset.inventory_number || '') : (asset.inventory_number || '')}
              onChange={handleChange}
              fullWidth
              margin="dense"
              // Отображаем ошибки валидации только в режиме редактирования
              error={!!(isEditing && editErrors.inventory_number)}
              helperText={isEditing ? editErrors.inventory_number : null}
              disabled={!currentUser || currentUser.role !== 'admin'}
            />
          </Grid>

          {/* === Пара 2: Тип устройства | Фирма === */}
          <Grid item xs={12} sm={6}>
            {/* Тип устройства (обязательное поле) */}
            <FormControl
              fullWidth
              margin="dense"
              // Отображаем ошибки валидации только в режиме редактирования
              error={!!(isEditing && editErrors.type_id)}
            >
              <InputLabel id="edit-type-label">Тип устройства *</InputLabel>
              <Select
                labelId="edit-type-label"
                label="Тип устройства *"
                name="type_id"
                // Используем handleTypeChange для специфической логики смены типа
                value={isEditing ? (editedAsset.type_id || '') : (asset.type_id || '')}
                onChange={handleTypeChange}
                disabled={!currentUser || currentUser.role !== 'admin'}
              >
                {types.map(type => (
                  <MenuItem key={type.id} value={type.id}>
                    {type.name}
                  </MenuItem>
                ))}
              </Select>
              {isEditing && editErrors.type_id && (
                <FormHelperText>{editErrors.type_id}</FormHelperText>
              )}
            </FormControl>
          </Grid>

          <Grid item xs={12} sm={6}>
            {/* Фирма */}
            <TextField
              label="Фирма"
              name="brand"
              value={isEditing ? (editedAsset.brand || '') : (asset.brand || '')}
              onChange={handleChange}
              fullWidth
              margin="dense"
              disabled={!currentUser || currentUser.role !== 'admin'}
            />
          </Grid>

          {/* === Пара 3: Модель | Динамическое поле 1 (если есть) === */}
          <Grid item xs={12} sm={6}>
            {/* Модель */}
            <TextField
              label="Модель"
              name="model"
              value={isEditing ? (editedAsset.model || '') : (asset.model || '')}
              onChange={handleChange}
              fullWidth
              margin="dense"
              disabled={!currentUser || currentUser.role !== 'admin'}
            />
          </Grid>

          {/* Динамическое поле 1 (если есть) */}
          {/* Предполагается, что renderDynamicField рендерит Grid item */}
          {/* Если dynamicFields.length > 0, рендерим первое поле здесь */}
          {/* Для простоты предположим, что dynamicFields - это массив объектов */}
          {/* и renderDynamicField уже учитывает Grid item */}
          {/* В реальности, логика может быть сложнее для точного размещения в сетке */}
          {/* Пока оставим как есть, предполагая, что renderDynamicField корректно обрабатывает это */}
          {/* {dynamicFields.length > 0 && renderDynamicField(dynamicFields[0])} */}

          {/* === Динамические поля === */}
          {/* Рендерим все оставшиеся динамические поля */}
          {/* Предполагается, что renderDynamicField внутри себя оборачивает содержимое в Grid item */}
          {/* и корректно обрабатывает размещение в сетке */}
          {dynamicFields.map(renderDynamicField)}

          {/* === Пара 4: Статус | Фактический пользователь === */}
          <Grid item xs={12} sm={6}>
            {/* Статус (обязательное поле) */}
            <FormControl
              fullWidth
              margin="dense"
              error={!!(isEditing && editErrors.status_id)}
            >
              <InputLabel id="edit-status-label">Статус *</InputLabel>
              <Select
                labelId="edit-status-label"
                label="Статус *"
                name="status_id"
                value={isEditing ? (editedAsset.status_id || '') : (asset.status_id || '')}
                onChange={handleChange}
                disabled={!currentUser || currentUser.role !== 'admin'}
              >
                {statuses.map(stat => (
                  <MenuItem key={stat.id} value={stat.id}>
                    {stat.name}
                  </MenuItem>
                ))}
              </Select>
              {isEditing && editErrors.status_id && (
                <FormHelperText>{editErrors.status_id}</FormHelperText>
              )}
            </FormControl>
          </Grid>

          <Grid item xs={12} sm={6}>
            {/* Фактический пользователь */}
            <TextField
              label="Фактический пользователь"
              name="actual_user"
              value={isEditing ? (editedAsset.actual_user || '') : (asset.actual_user || '')}
              onChange={handleChange}
              fullWidth
              margin="dense"
              disabled={!currentUser || currentUser.role !== 'admin'}
            />
          </Grid>

          {/* === Пара 5: Ответственный | Отдел (подразделение) === */}
          <Grid item xs={12} sm={6}>
            {/* Ответственный (обязательное поле) */}
            <FormControl
              fullWidth
              margin="dense"
              error={!!(isEditing && editErrors.responsible_person)}
            >
              <InputLabel id="edit-responsible-person-label">Ответственный *</InputLabel>
              <Select
                labelId="edit-responsible-person-label"
                label="Ответственный *"
                name="responsible_person"
                value={isEditing ? (editedAsset.responsible_person || '') : (asset.responsible_person || '')}
                onChange={handleChange}
                disabled={!currentUser || currentUser.role !== 'admin'}
              >
                {employees.map(emp => (
                  <MenuItem key={emp.id} value={emp.id}>
                    {emp.full_name || emp.name} {/* Используем full_name, если доступно */}
                  </MenuItem>
                ))}
              </Select>
              {isEditing && editErrors.responsible_person && (
                <FormHelperText>{editErrors.responsible_person}</FormHelperText>
              )}
            </FormControl>
          </Grid>

          <Grid item xs={12} sm={6}>
            {/* Отдел (подразделение) - ЗАБЛОКИРОВАНО для редактирования */}
            <FormControl fullWidth margin="dense">
              <InputLabel id="edit-department-label">Отдел (подразделение)</InputLabel>
              <Select
                labelId="edit-department-label"
                label="Отдел (подразделение)"
                name="department_id"
                // Значение берется из asset, так как поле заблокировано
                value={asset.department_id || ''} // Используем asset, так как поле заблокировано
                onChange={handleChange} // Обработчик может быть, если нужно, но значение не изменится
                // Поле заблокировано для редактирования
                disabled
                // Стилизация заблокированного поля
                sx={{
                  // Фон серый, как у disabled поля
                  backgroundColor: 'action.disabledBackground',
                  // Цвет текста стандартный для disabled
                  '& .MuiSelect-select': { color: 'text.disabled' },
                  // Цвет рамки стандартный для disabled
                  '& .MuiOutlinedInput-notchedOutline': { borderColor: 'action.disabled' },
                }}
              >
                {departments.map(dept => (
                  <MenuItem key={dept.id} value={dept.id}>
                    {dept.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          {/* === Пара 6: Помещение | Дата покупки === */}
          <Grid item xs={12} sm={6}>
            {/* Помещение */}
            <TextField
              label="Помещение"
              name="room"
              value={isEditing ? (editedAsset.room || '') : (asset.room || '')}
              onChange={handleChange}
              fullWidth
              margin="dense"
              disabled={!currentUser || currentUser.role !== 'admin'}
            />
          </Grid>

          <Grid item xs={12} sm={6}>
            {/* Дата покупки (обязательное поле) */}
            <TextField
              label="Дата покупки *"
              name="purchase_date"
              type="date"
              value={isEditing ? (editedAsset.purchase_date || '') : (asset.purchase_date || '')}
              onChange={handleChange}
              fullWidth
              margin="dense"
              // InputLabelProps нужен для корректного отображения плавающей метки
              InputLabelProps={{
                shrink: true,
              }}
              // Отображаем ошибки валидации только в режиме редактирования
              error={!!(isEditing && editErrors.purchase_date)}
              helperText={isEditing ? editErrors.purchase_date : null}
              disabled={!currentUser || currentUser.role !== 'admin'}
            />
          </Grid>

          {/* === Комментарий (занимает всю ширину) === */}
          <Grid item xs={12}>
            {/* Комментарий */}
            <TextField
              label="Комментарий"
              name="comments"
              value={isEditing ? (editedAsset.comments || '') : (asset.comments || '')}
              onChange={handleChange}
              fullWidth
              multiline
              rows={3}
              margin="dense"
              disabled={!currentUser || currentUser.role !== 'admin'}
            />
          </Grid>

          {/* === QR-код (занимает всю ширину и центрируется) === */}
          {/* Помещаем QR-код в отдельный Grid item */}
          <Grid item xs={12} display="flex" justifyContent="center" mt={2}>
            {qrCodeUrl ? (
              <img
                src={qrCodeUrl}
                alt={`QR-код для актива ${asset.inventory_number}`}
                style={{ maxWidth: '200px', height: 'auto' }}
              />
            ) : (
              <Typography>Загрузка QR-кода...</Typography>
            )}
          </Grid>






              {/* <<<--- ИЗМЕНЕНО: QR-код в той же карточке, без заголовка */}
              <Grid item xs={12} display="flex" justifyContent="center" mt={2}>
                {qrCodeUrl ? (
                  <img
                    src={qrCodeUrl}
                    alt={`QR-код для актива ${asset.inventory_number}`}
                    style={{ maxWidth: '200px', height: 'auto' }}
                  />
                ) : (
                  <Typography>Загрузка QR-кода...</Typography>
                )}
              </Grid>
            </Grid>
          </Box>

        <Box mt={0.5} display="flex" justifyContent="center">
          <Button variant="contained" color="primary" onClick={handlePrintQRCode}>
            Печать QR
          </Button>
        </Box>        

        <Box mt={4.5} display="flex" justifyContent="space-between" alignItems="center">
          <Button onClick={() => navigate('/assets')} variant="outlined">
            Назад
          </Button>

          <Box>
            {currentUser && currentUser.role === 'admin' && (
              <>
                <Button
                  variant="contained"
                  color="error"
                  onClick={handleOpenDeleteDialog}
                  disabled={!currentUser || currentUser.role !== 'admin'}
                  sx={{ mr: '16px' }}
                >
                  Удалить
                </Button>


                <Button
                  variant="contained"
                  color="success"
                  onClick={handleSave}
                  disabled={!currentUser || currentUser.role !== 'admin'}

                >
                  Сохранить
                </Button>
              </>
            )}
          </Box>
        </Box>

      {/* Диалог подтверждения удаления */}
      <Dialog
        open={openDeleteDialog}
        onClose={handleCloseDeleteDialog}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{"Подтвердите удаление"}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Вы уверены, что хотите удалить актив "{asset.inventory_number}"?
            Это действие нельзя отменить.
          </DialogContentText>
          {deleteError && (
            <Alert severity="error" style={{ marginTop: '10px' }}>
              {deleteError}
            </Alert>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDeleteDialog} color="primary">
            Отмена
          </Button>
          <Button onClick={handleDelete} color="error" autoFocus>
            Удалить
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default AssetDetails;
