import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableRow,
} from '@mui/material';

const ChangesList = () => {
  const [changes, setChanges] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/changes')
      .then(res => setChanges(res.data))
      .catch(err => console.error('Ошибка загрузки истории:', err));
  }, []);

  // Форматируем дату под нужный вид: ДД.ММ.ГГГГ ЧЧ:ММ:СС
  const formatDate = (dateString) => {
    if (!dateString) return '-';
    const date = new Date(dateString);
    const day = String(date.getDate()).padStart(2, '0');
    const month = String(date.getMonth() + 1).padStart(2, '0'); // Месяцы с 0
    const year = date.getFullYear();
    const hours = String(date.getHours()).padStart(2, '0');
    const minutes = String(date.getMinutes()).padStart(2, '0');
    const seconds = String(date.getSeconds()).padStart(2, '0');

    return `${day}.${month}.${year} ${hours}:${minutes}:${seconds}`;
  };

  return (
    <Container maxWidth="lg">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom>
          История изменений
        </Typography>

        <Table size="small">
          <TableBody>
            {changes.length > 0 ? (
              changes.map((change) => {
                // Определяем тип действия
                const isCreate = change.action === 'created';
                const isUpdate = change.action === 'updated';
                const isDelete = change.action === 'deleted';

                let displayOld = change.old_value || '-';
                let displayNew = change.new_value || '-';

                if (isCreate) {
                  displayNew = '—';
                }

                if (isDelete) {
                  displayOld = '—';
                  displayNew = '—';
                }

                return (
                  <TableRow key={change.id}>
                    <TableCell>
                      <strong>Дата:</strong> {formatDate(change.changed_at)}<br />
                      <strong>Инвентарный номер:</strong> {change.inventory_number || '-'}<br />
                      <strong>Наименование:</strong> {change.asset_name || '-'}<br />
                      <strong>Было:</strong> {displayOld}<br />
                      <strong>Стало:</strong> {displayNew}<br />
                    </TableCell>
                  </TableRow>
                );
              })
            ) : (
              <TableRow>
                <TableCell align="center">Нет записей об изменениях</TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
};

export default ChangesList;
