// frontend/src/components/AssetList.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AssetList = () => {
  const [assets, setAssets] = useState([]);

  useEffect(() => {
    // Запрос к API для получения списка активов
    axios.get('http://localhost:5000/api/assets/')
      .then(res => {
        setAssets(res.data);
      })
      .catch(err => {
        console.error('Ошибка при получении активов:', err);
      });
  }, []);

  return (
    <div>
      <h2>Список активов</h2>
      {assets.length === 0 ? (
        <p>Активов пока нет.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Инвентарный номер</th>
              <th>Наименование</th>
              <th>Местоположение</th>
              <th>Статус</th>
            </tr>
          </thead>
          <tbody>
            {assets.map(asset => (
              <tr key={asset.id}>
                <td>{asset.inventory_number}</td>
                <td>{asset.name}</td>
                <td>{asset.location}</td>
                <td>{asset.status_id}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default AssetList;
