// frontend/src/components/AddAsset.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  TextField,
  Button,
  Box
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

const AddAsset = () => {
  const navigate = useNavigate();
  const [asset, setAsset] = useState({
    serial_number: '',
    inventory_number: '',
    name: '',
    category_id: '', // Обязательное поле
    location: '',
    responsible_person: '',
    actual_user: '',
    comments: '',
    status_id: '', // Обязательное поле
  });

  const handleSubmit = async () => {
    if (!asset.inventory_number || !asset.name || !asset.category_id || !asset.status_id) {
      alert('Пожалуйста, заполните все обязательные поля');
      return;
    }

    try {
      const res = await axios.post('http://localhost:5000/api/assets', asset);
      alert('Актив успешно добавлен');
      navigate(`/assets/${res.data.id}`);
    } catch (err) {
      alert('Ошибка при добавлении актива');
      console.error('Ошибка при отправке данных:', err.response?.data || err.message);
    }
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5">Добавить новый актив</Typography>
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={asset.serial_number}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Инвентарный номер"
              name="inventory_number"
              value={asset.inventory_number}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Наименование"
              name="name"
              value={asset.name}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Местоположение"
              name="location"
              value={asset.location}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Ответственный"
              name="responsible_person"
              value={asset.responsible_person}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Фактический пользователь"
              name="actual_user"
              value={asset.actual_user}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Комментарии"
              name="comments"
              value={asset.comments}
              onChange={handleChange}
              fullWidth
              multiline
              rows={4}
              margin="normal"
            />
            <TextField
              label="Статус"
              name="status_id"
              value={asset.status_id}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
          </Grid>
        </Grid>

        <Box mt={3} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={() => navigate(-1)}>
            Отмена
          </Button>
          <Button variant="contained" color="success" onClick={handleSubmit}>
            Создать актив
          </Button>
        </Box>
      </Paper>
    </Container>
  );
};

export default AddAsset;
