// frontend/src/components/AssetDetails.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  TextField,
  Button,
  Box,
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom'; // Импортируем хуки
import { QRCodeSVG } from 'qrcode.react'; // Исправленный импорт

const AssetDetails = ({ currentUser }) => {
  const { id } = useParams(); // Получаем id из URL
  const navigate = useNavigate();
  const [asset, setAsset] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAsset = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/assets/${id}`);
        setAsset(res.data);
      } catch (error) {
        console.error('Ошибка загрузки актива:', error);
      } finally {
        setLoading(false);
      }
    };

    if (id) {
      fetchAsset();
    }
  }, [id]);

  if (loading) {
    return <Box p={3}>Загрузка...</Box>;
  }

  if (!asset) {
    return <Box p={3}>Актив не найден</Box>;
  }

  const handleChange = (e) => {
    setAsset({
      ...asset,
      [e.target.name]: e.target.value
    });
  };

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5000/api/assets/${id}`, asset);
      alert('Данные успешно сохранены');
    } catch (err) {
      alert('Ошибка при сохранении');
      console.error(err);
    }
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" gutterBottom>{asset.name}</Typography>

        <Grid container spacing={2}>
          {/* ... */}
        </Grid>

        {/* QR-код */}
        <Box mt={3} display="flex" justifyContent="center">
          <div id="qrcode">
            <QRCodeSVG
              value={asset.inventory_number || 'IT-Актив'}
              size={200}
              bgColor="#ffffff"
              fgColor="#000000"
              level="H"
            />
          </div>
        </Box>

        {/* Кнопки управления */}
        <Box mt={3} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={() => navigate(-1)}>
            Назад к списку
          </Button>

          {currentUser && currentUser.role === 'admin' && (
            <Button variant="contained" color="success" onClick={handleSave}>
              Сохранить изменения
            </Button>
          )}
        </Box>
      </Paper>
    </Container>
  );
};

export default AssetDetails;
