// frontend/src/components/AssetDetails.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  TextField,
  Button,
  Box
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react';

const AssetDetails = ({ currentUser }) => {
  const { id } = useParams(); // Получаем id из URL
  const navigate = useNavigate();
  const [asset, setAsset] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (id) {
      axios.get(`http://localhost:5000/api/assets/${id}`)
        .then(res => {
          setAsset(res.data);
          setLoading(false);
        })
        .catch(err => {
          console.error('Ошибка загрузки актива:', err);
          setLoading(false);
        });
    }
  }, [id]);

  const handleChange = (e) => {
    setAsset({
      ...asset,
      [e.target.name]: e.target.value
    });
  };

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5000/api/assets/${id}`, asset);
      alert('Данные сохранены');
    } catch (err) {
      alert('Ошибка при сохранении изменений');
      console.error(err);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Вы уверены, что хотите удалить этот актив?')) {
      return;
    }

    try {
      await axios.delete(`http://localhost:5000/api/assets/${id}`);
      alert('Актив успешно удален');
      navigate('/');
    } catch (err) {
      alert('Ошибка при удалении актива');
      console.error(err);
    }
  };

  if (loading) {
    return <Box p={3}>Загрузка...</Box>;
  }

  if (!asset) {
    return <Box p={3}>Актив не найден</Box>;
  }

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" gutterBottom>Редактировать актив: {asset.name}</Typography>

        <Grid container spacing={2}>
          <Grid item xs={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={asset.serial_number || ''}
              onChange={handleChange}
              disabled={!currentUser || currentUser.role !== 'admin'}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Инвентарный номер"
              name="inventory_number"
              value={asset.inventory_number || ''}
              onChange={handleChange}
              disabled={!currentUser || currentUser.role !== 'admin'}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Наименование"
              name="name"
              value={asset.name || ''}
              onChange={handleChange}
              disabled={!currentUser || currentUser.role !== 'admin'}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Местоположение"
              name="location"
              value={asset.location || ''}
              onChange={handleChange}
              disabled={!currentUser || currentUser.role !== 'admin'}
              fullWidth
              margin="normal"
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Ответственный"
              name="responsible_person"
              value={asset.responsible_person || ''}
              onChange={handleChange}
              disabled={!currentUser || currentUser.role !== 'admin'}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Фактический пользователь"
              name="actual_user"
              value={asset.actual_user || ''}
              onChange={handleChange}
              disabled={!currentUser || currentUser.role !== 'admin'}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Комментарии"
              name="comments"
              value={asset.comments || ''}
              onChange={handleChange}
              disabled={!currentUser || currentUser.role !== 'admin'}
              fullWidth
              multiline
              rows={4}
              margin="normal"
            />
            <TextField
              label="Статус"
              name="status_id"
              value={asset.status_id || ''}
              onChange={handleChange}
              disabled={!currentUser || currentUser.role !== 'admin'}
              fullWidth
              margin="normal"
            />
          </Grid>
        </Grid>

        {/* QR-код */}
        <Box mt={3} display="flex" justifyContent="center">
          <div id="qrcode">
            <QRCodeSVG
              value={asset.inventory_number || 'IT-Актив'}
              size={200}
              bgColor="#ffffff"
              fgColor="#000000"
              level="H"
            />
          </div>
        </Box>

        {/* Кнопки управления */}
        <Box mt={3} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={() => navigate(-1)}>
            Назад к списку
          </Button>

          {currentUser && currentUser.role === 'admin' && (
            <>
              <Button variant="contained" color="success" onClick={handleSave}>
                Сохранить изменения
              </Button>

              <Button
                variant="contained"
                color="error"
                onClick={handleDelete}
              >
                Удалить актив
              </Button>
            </>
          )}
        </Box>
      </Paper>
    </Container>
  );
};

export default AssetDetails;
