// frontend/src/components/Directory.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  Table,
  TableBody,
  TableCell,
  TableRow,
  TextField,
  Button,
  Tooltip,
  IconButton,
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

const Directory = () => {
  const [categories, setCategories] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [employees, setEmployees] = useState([]);

  const [newCategory, setNewCategory] = useState('');
  const [newStatus, setNewStatus] = useState('');
  const [newDepartment, setNewDepartment] = useState('');
  const [newEmployeeName, setNewEmployeeName] = useState('');
  const [newEmployeeDepartmentId, setNewEmployeeDepartmentId] = useState('');

  // Функция для загрузки всех справочных данных
  const fetchAllData = async () => {
    try {
      const [catsRes, statsRes, depsRes, empsRes] = await Promise.all([
        axios.get('http://localhost:5000/api/categories'),
        axios.get('http://localhost:5000/api/statuses'),
        axios.get('http://localhost:5000/api/departments'),
        axios.get('http://localhost:5000/api/employees'),
      ]);

      setCategories(catsRes.data);
      setStatuses(statsRes.data);
      setDepartments(depsRes.data);
      setEmployees(empsRes.data);
    } catch (err) {
      console.error('Ошибка загрузки справочных данных:', err);
    }
  };

  useEffect(() => {
    fetchAllData();
  }, []);

  const handleAddCategory = async () => {
    if (!newCategory.trim()) return alert('Введите название категории');
    try {
      const res = await axios.post('http://localhost:5000/api/categories', { name: newCategory });
      setCategories([...categories, res.data]);
      setNewCategory('');
    } catch (err) {
      alert('Ошибка при добавлении категории');
      console.error(err);
    }
  };

  const handleDeleteCategory = async (id) => {
    if (!window.confirm('Удалить эту категорию?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/categories/${id}`);
      fetchAllData();
    } catch (err) {
      alert('Ошибка при удалении категории');
      console.error(err);
    }
  };

  const handleAddStatus = async () => {
    if (!newStatus.trim()) return alert('Введите статус');
    try {
      const res = await axios.post('http://localhost:5000/api/statuses', { name: newStatus });
      setStatuses([...statuses, res.data]);
      setNewStatus('');
    } catch (err) {
      alert('Ошибка при добавлении статуса');
      console.error(err);
    }
  };

  const handleDeleteStatus = async (id) => {
    if (!window.confirm('Удалить этот статус?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/statuses/${id}`);
      fetchAllData();
    } catch (err) {
      alert('Ошибка при удалении статуса');
      console.error(err);
    }
  };

  const handleAddDepartment = async () => {
    if (!newDepartment.trim()) return alert('Введите название отдела');
    try {
      const res = await axios.post('http://localhost:5000/api/departments', { name: newDepartment });
      setDepartments([...departments, res.data]);
      setNewDepartment('');
    } catch (err) {
      alert('Ошибка при добавлении отдела');
      console.error(err);
    }
  };

  const handleDeleteDepartment = async (id) => {
    if (!window.confirm('Удалить этот отдел?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/departments/${id}`);
      fetchAllData();
    } catch (err) {
      alert('Ошибка при удалении отдела');
      console.error(err);
    }
  };

  const handleAddEmployee = async () => {
    if (!newEmployeeName || !newEmployeeDepartmentId) {
      return alert('Введите ФИО сотрудника и выберите отдел');
    }

    try {
      const res = await axios.post('http://localhost:5000/api/employees', {
        name: newEmployeeName,
        department_id: parseInt(newEmployeeDepartmentId),
      });
      setEmployees([...employees, res.data]);
      setNewEmployeeName('');
      setNewEmployeeDepartmentId('');
    } catch (err) {
      alert('Ошибка при добавлении сотрудника');
      console.error(err);
    }
  };

  const handleDeleteEmployee = async (id) => {
    if (!window.confirm('Удалить этого сотрудника?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/employees/${id}`);
      fetchAllData();
    } catch (err) {
      alert('Ошибка при удалении сотрудника');
      console.error(err);
    }
  };

  return (
    <Container maxWidth="lg">
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" gutterBottom>Справочники</Typography>

        {/* Категории */}
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <Typography variant="h6">Категории</Typography>
            <Box display="flex" gap={2} mb={2}>
              <TextField
                label="Новая категория"
                value={newCategory}
                onChange={(e) => setNewCategory(e.target.value)}
                size="small"
                fullWidth
              />
              <Button variant="contained" onClick={handleAddCategory}>
                Добавить
              </Button>
            </Box>
            <Table size="small">
              <TableBody>
                {categories.map(cat => (
                  <TableRow key={cat.id}>
                    <TableCell>{cat.name}</TableCell>
                    <TableCell align="right">
                      <Tooltip title="Удалить">
                        <IconButton
                          color="error"
                          onClick={() => handleDeleteCategory(cat.id)}
                          style={{ visibility: 'hidden' }}
                          onMouseEnter={(e) => e.target.style.visibility = 'visible'}
                          onMouseLeave={(e) => e.target.style = 'visibility: hidden;'}
                        >
                          <DeleteIcon />
                        </IconButton>
                      </Tooltip>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Grid>

          {/* Статусы */}
          <Grid item xs={6}>
            <Typography variant="h6">Статусы</Typography>
            <Box display="flex" gap={2} mb={2}>
              <TextField
                label="Новый статус"
                value={newStatus}
                onChange={(e) => setNewStatus(e.target.value)}
                size="small"
                fullWidth
              />
              <Button variant="contained" onClick={handleAddStatus}>
                Добавить
              </Button>
            </Box>
            <Table size="small">
              <TableBody>
                {statuses.map(stat => (
                  <TableRow key={stat.id}>
                    <TableCell>{stat.name}</TableCell>
                    <TableCell align="right">
                      <Tooltip title="Удалить">
                        <IconButton
                          color="error"
                          onClick={() => handleDeleteStatus(stat.id)}
                          style={{ visibility: 'hidden' }}
                          onMouseEnter={(e) => e.target.style.visibility = 'visible'}
                          onMouseLeave={(e) => e.target.style = 'visibility: hidden;'}
                        >
                          <DeleteIcon />
                        </IconButton>
                      </Tooltip>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Grid>

          {/* Отделы */}
          <Grid item xs={6}>
            <Typography variant="h6">Отделы</Typography>
            <Box display="flex" gap={2} mb={2}>
              <TextField
                label="Новый отдел"
                value={newDepartment}
                onChange={(e) => setNewDepartment(e.target.value)}
                size="small"
                fullWidth
              />
              <Button variant="contained" onClick={handleAddDepartment}>
                Добавить
              </Button>
            </Box>
            <Table size="small">
              <TableBody>
                {departments.map(dept => (
                  <TableRow key={dept.id}>
                    <TableCell>{dept.name}</TableCell>
                    <TableCell align="right">
                      <Tooltip title="Удалить">
                        <IconButton
                          color="error"
                          onClick={() => handleDeleteDepartment(dept.id)}
                          style={{ visibility: 'hidden' }}
                          onMouseEnter={(e) => e.target.style.visibility = 'visible'}
                          onMouseLeave={(e) => e.target.style = 'visibility: hidden;'}
                        >
                          <DeleteIcon />
                        </IconButton>
                      </Tooltip>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Grid>

          {/* Сотрудники */}
          <Grid item xs={6}>
            <Typography variant="h6">Сотрудники</Typography>
            <Box display="flex" gap={2} mb={2}>
              <TextField
                label="ФИО"
                value={newEmployeeName}
                onChange={(e) => setNewEmployeeName(e.target.value)}
                size="small"
                fullWidth
              />
              <TextField
                select
                label="Отдел"
                value={newEmployeeDepartmentId}
                onChange={(e) => setNewEmployeeDepartmentId(e.target.value)}
                size="small"
                sx={{ minWidth: 150 }}
              >
                {departments.map(d => (
                  <MenuItem key={d.id} value={d.id}>
                    {d.name}
                  </MenuItem>
                ))}
              </TextField>
              <Button variant="contained" onClick={handleAddEmployee}>
                Добавить
              </Button>
            </Box>
            <Table size="small">
              <TableBody>
                {employees.map(emp => {
                  const dept = departments.find(d => d.id === emp.department_id);
                  return (
                    <TableRow key={emp.id}>
                      <TableCell>{emp.name}</TableCell>
                      <TableCell>{dept?.name || 'Не указано'}</TableCell>
                      <TableCell align="right">
                        <Tooltip title="Удалить">
                          <IconButton
                            color="error"
                            onClick={() => handleDeleteEmployee(emp.id)}
                            style={{ visibility: 'hidden' }}
                            onMouseEnter={(e) => e.target.style.visibility = 'visible'}
                            onMouseLeave={(e) => e.target.style = 'visibility: hidden;'}
                          >
                            <DeleteIcon />
                          </IconButton>
                        </Tooltip>
                      </TableCell>
                    </TableRow>
                  );
                })}
              </TableBody>
            </Table>
          </Grid>
        </Grid>
      </Paper>
    </Container>
  );
};

export default Directory;
