// frontend/src/components/AssetDetails.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Divider,
  Grid,
  Box,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Button,
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react';

const AssetDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [asset, setAsset] = useState(null);
  const [categories, setCategories] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [isAdmin] = useState(true); // Замени на реальную проверку прав доступа

  useEffect(() => {
    // Загружаем данные актива
    axios.get(`http://localhost:5000/api/assets/${id}`)
      .then(res => setAsset(res.data))
      .catch(err => console.error('Ошибка загрузки данных:', err));

    // Загружаем справочники
    axios.get('http://localhost:5000/api/categories')
      .then(res => setCategories(res.data));

    axios.get('http://localhost:5000/api/statuses')
      .then(res => setStatuses(res.data));
  }, [id]);

  const handleChange = (e) => {
    setAsset({ ...asset, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5000/api/assets/${id}`, asset);
      setIsEditing(false);
      alert('Данные сохранены');
    } catch (err) {
      alert('Ошибка сохранения');
      console.error(err);
    }
  };

  if (!asset) {
    return <Box p={3}>Загрузка...</Box>;
  }

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" gutterBottom>Карточка оборудования</Typography>
        <Divider style={{ marginBottom: '20px' }} />

        <Grid container spacing={2}>
          <Grid item xs={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={asset.serial_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
            />
            <TextField
              label="Инвентарный номер"
              name="inventory_number"
              value={asset.inventory_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
            />
            <TextField
              label="Наименование"
              name="name"
              value={asset.name || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
            />
            <FormControl fullWidth margin="normal">
              <InputLabel id="category-label">Категория</InputLabel>
              <Select
                labelId="category-label"
                name="category_id"
                value={asset.category_id || ''}
                onChange={handleChange}
                label="Категория"
                disabled={!isEditing}
              >
                {categories.map(cat => (
                  <MenuItem key={cat.id} value={cat.id}>{cat.name}</MenuItem>
                ))}
              </Select>
            </FormControl>
            <TextField
              label="Местоположение"
              name="location"
              value={asset.location || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
            />
          </Grid>

          <Grid item xs={6}>
            <TextField
              label="Ответственный"
              name="responsible_person"
              value={asset.responsible_person || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
            />
            <TextField
              label="Фактический пользователь"
              name="actual_user"
              value={asset.actual_user || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
            />
            <FormControl fullWidth margin="normal">
              <InputLabel id="status-label">Статус</InputLabel>
              <Select
                labelId="status-label"
                name="status_id"
                value={asset.status_id || ''}
                onChange={handleChange}
                label="Статус"
                disabled={!isEditing}
              >
                {statuses.map(stat => (
                  <MenuItem key={stat.id} value={stat.id}>{stat.name}</MenuItem>
                ))}
              </Select>
            </FormControl>
            <TextField
              label="Комментарии"
              name="comments"
              value={asset.comments || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              multiline
              rows={4}
              disabled={!isEditing}
            />
          </Grid>
        </Grid>

        {/* QR-код */}
        <Box mt={3} display="flex" justifyContent="center">
          <QRCodeSVG
            value={asset.inventory_number || 'IT-Актив'}
            size={200}
            bgColor="#ffffff"
            fgColor="#000000"
            level="H"
          />
        </Box>

        {/* Кнопки управления */}
        <Box mt={3} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={() => navigate('/')}>
            Назад к списку
          </Button>

          {isAdmin && (
            <>
              {!isEditing ? (
                <Button variant="contained" color="primary" onClick={() => setIsEditing(true)}>
                  Редактировать
                </Button>
              ) : (
                <Button variant="contained" color="success" onClick={handleSave}>
                  Сохранить
                </Button>
              )}
            </>
          )}
        </Box>
      </Paper>
    </Container>
  );
};

export default AssetDetails;
