// frontend/src/App.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import AssetList from './components/AssetList';
import AssetDetails from './components/AssetDetails';
import Sidebar from './components/Sidebar';
import Header from './components/Header';

function App() {
  const [currentUser, setCurrentUser] = useState(null);

  const handleLogin = (user) => {
    setCurrentUser(user);
  };

  const handleLogout = () => {
    setCurrentUser(null);
  };

  // Защищённый маршрут для админов
  const ProtectedRoute = ({ children }) => {
    if (!currentUser) {
      return <Navigate to="/login" replace />;
    }
    return children;
  };

  // Только для чтения — доступен всем
  const ReadOnlyRoute = ({ children }) => {
    if (!currentUser) {
      return <Navigate to="/login" replace />;
    }
    return children;
  };

  return (
    <Router>
      <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
        {/* Шапка с именем пользователя */}
        {currentUser && (
          <Header
            username={currentUser.username}
            role={currentUser.role}
            onLogout={handleLogout}
          />
        )}

        <div style={{ display: 'flex', flexGrow: 1 }}>
          {/* Боковое меню */}
          {currentUser && <Sidebar role={currentUser.role} />}

          {/* Основной контент */}
          <div style={{
            flex: 1,
            backgroundColor: '#f9f9f9',
            padding: '20px'
          }}>
            <Routes>
              {/* Маршрут входа */}
              <Route path="/login" element={!currentUser ? <Login onLogin={handleLogin} /> : <Navigate to="/" />} />

              {/* Главная страница (только для залогиненных пользователей) */}
              <Route path="/" element={
                <ProtectedRoute>
                  <AssetList currentUser={currentUser} />
                </ProtectedRoute>
              } />

              {/* Детальная информация об активе */}
              <Route path="/assets/:id" element={
                <ReadOnlyRoute>
                  <AssetDetails currentUser={currentUser} />
                </ReadOnlyRoute>
              } />

              {/* Резервный маршрут */}
              <Route path="*" element={<Navigate to="/" />} />
              <Route path="/assets/new" element={<AddAsset />} />
            </Routes>
          </div>
        </div>
      </div>
    </Router>
  );
}

export default App;
