import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableContainer,
  TableHead,
  TableRow,
  TableCell,
  TableBody
} from '@mui/material';

const ChangesList = () => {
  const [changes, setChanges] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/changes')
      .then(res => setChanges(res.data))
      .catch(err => console.error('Ошибка загрузки истории:', err));
  }, []);

  return (
    <Container maxWidth="lg">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom>История изменений</Typography>

        <TableContainer>
          <Table size="small">
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Тип</TableCell>
                <TableCell>Название</TableCell>
                <TableCell>Инвентарный номер</TableCell>
                <TableCell>Статус</TableCell>
                <TableCell>Ответственный</TableCell>
                <TableCell>Отдел</TableCell>
                <TableCell>Кто изменил</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {changes.map(change => (
                <TableRow key={change.id}>
                  <TableCell>{new Date(change.change_date).toLocaleString()}</TableCell>
                  <TableCell>{change.type}</TableCell>
                  <TableCell>{change.name}</TableCell>
                  <TableCell>{change.inventory_number}</TableCell>
                  <TableCell>{change.status}</TableCell>
                  <TableCell>{change.responsible_person}</TableCell>
                  <TableCell>{change.department_id}</TableCell>
                  <TableCell>{change.changed_by}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
    </Container>
  );
};

export default ChangesList;
