import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
} from '@mui/material';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [assetCounts, setAssetCounts] = useState({}); // Кол-во активов на сотрудника
  const [totalAssetsByDept, setTotalAssetsByDept] = useState([]);

  // === Загрузка данных ===
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Загружаем сотрудников и отделы
        const empsRes = await axios.get('http://localhost:5000/api/employees');
        const depsRes = await axios.get('http://localhost:5000/api/departments');
        const emps = empsRes.data;
        const deps = depsRes.data;

        setEmployees(emps);
        setDepartments(deps);

        // Подсчёт активов по сотрудникам (через department_id + фильтр на клиенте)
        const assetCounts = {};
        const totalAssetsByDept = {};

        for (let dept of deps) {
          const deptId = dept.id;

          // Загружаем все активы отдела один раз
          const assetsRes = await axios.get(`http://localhost:5000/api/assets?department_id=${deptId}`);
          const assets = assetsRes.data;

          // Общее количество активов в отделе
          totalAssetsByDept[deptId] = assets.length;

          // Для каждого сотрудника этого отдела — фильтруем его активы
          for (let emp of emps.filter(e => e.department_id === deptId)) {
            const count = assets.filter(a => a.responsible_person === emp.name).length;
            assetCounts[emp.id] = count;
          }
        }

        setAssetCounts(assetCounts);
        setTotalAssetsByDept(totalAssetsByDept);

      } catch (err) {
        console.error('Ошибка при загрузке данных:', err);
      }
    };

    fetchData();
  }, []);

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>

        {/* Отображаем сотрудников по отделам */}
        {departments.map(dept => {
          const deptEmployees = employees.filter(emp => emp.department_id === dept.id);
          const totalAssets = deptEmployees.reduce((sum, emp) => sum + (assetCounts[emp.id] || 0), 0);

          if (deptEmployees.length === 0) {
            return null; // Не отображаем пустые отделы
          }

          return (
            <div key={dept.id}>
              <Typography
                variant="h6"
                gutterBottom
                style={{ fontWeight: 'bold', textAlign: 'center', fontSize: '25px' }}
              >
                {dept.name}
              </Typography>

              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell></TableCell>
                    <TableCell align="right" style={{ fontSize: '12px'}}>Оборудования</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {deptEmployees.map(emp => (
                    <TableRow key={emp.id}>
                      <TableCell>{emp.name}</TableCell>
                      <TableCell align="right">{assetCounts[emp.id] || 0}</TableCell>
                    </TableRow>
                  ))}

                  {/* Итоговая строка с суммой оборудования */}
                  <TableRow sx={{ backgroundColor: '#f0f0f0' }}>
                    <TableCell><strong>Всего</strong></TableCell>
                    <TableCell align="right"><strong>{totalAssetsByDept[dept.id] || 0}</strong></TableCell>
                  </TableRow>
                </TableBody>
              </Table>
            </div>
          );
        })}
      </Paper>
    </Container>
  );
};

export default EmployeeList;
