import React, { useState, useEffect } from 'react';
import {
  Container,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Typography
} from '@mui/material';
import axios from 'axios';

const MovesList = () => {
  const [moves, setMoves] = useState([]);
  const [filteredMoves, setFilteredMoves] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortColumn, setSortColumn] = useState('date');
  const [sortOrder, setSortOrder] = useState('asc');



  // Фильтры
  const [sortConfig, setSortConfig] = useState({
    key: null,
    direction: 'asc'
  });

  // Получаем данные с сервера
  useEffect(() => {
    const fetchMoves = async () => {
      const res = await axios.get('http://localhost:5000/api/moves');
      setMoves(res.data);
      setFilteredMoves(res.data);
    };
    fetchMoves();
  }, []);

  // Универсальный поиск по всем полям
  useEffect(() => {
    if (!searchTerm.trim()) {
      setFilteredMoves(moves);
      return;
    }

    const term = searchTerm.toLowerCase();

    const filtered = moves.filter(move =>
      move.inventory_number?.toLowerCase().includes(term) ||
      move.asset_name?.toLowerCase().includes(term) ||
      move.from_room?.toLowerCase().includes(term) ||
      move.to_room?.toLowerCase().includes(term) ||
      new Date(move.date).toLocaleDateString().includes(term)
    );

    setFilteredMoves(filtered);
  }, [searchTerm, moves]);

  // Фильтрация по строке поиска
  const filteredAssets = assets.filter(asset => {
    const searchLower = searchTerm.toLowerCase();
    
    const assetFieldsToSearch = [
      asset.inventory_number,
      `${asset.type} ${asset.brand} ${asset.model}`,
      getStatusName(asset.status_id),
      formatEmployeeName(asset.responsible_person),
      getDepartmentName(asset.department_id),
      asset.room || '',
      formatDate(asset.purchase_date),
    ].join(' ').toLowerCase();
    
    return assetFieldsToSearch.includes(searchLower);
  });
  
  // Сортировка активов
  const sortedAssets = [...filteredAssets].sort((a, b) => {
    let comparison = 0;
    
    switch (sortColumn) {
      case 'inventory_number':
        comparison = a.inventory_number.localeCompare(b.inventory_number, undefined, { numeric: true });
        break;
        case 'name':
          comparison = a.type.localeCompare(b.type, undefined, { sensitivity: 'base' });
          break;
          case 'brand':
            comparison = a.brand.localeCompare(b.brand, undefined, { sensitivity: 'base' });
            break;
            case 'model':
              comparison = a.model.localeCompare(b.model, undefined, { sensitivity: 'base' });
              break;
              case 'status':
                const statusA = getStatusName(a.status_id).toLowerCase();
                const statusB = getStatusName(b.status_id).toLowerCase();
                comparison = statusA.localeCompare(statusB);
                break;
                case 'responsible':
                  const empA = formatEmployeeName(a.responsible_person).toLowerCase();
                  const empB = formatEmployeeName(b.responsible_person).toLowerCase();
                  comparison = empA.localeCompare(empB);
                  break;
                  case 'department':
                    const deptA = getDepartmentName(a.department_id).toLowerCase();
                    const deptB = getDepartmentName(b.department_id).toLowerCase();
                    comparison = deptA.localeCompare(deptB);
                    break;
                    case 'room':
                      const roomA = (a.room || '').toLowerCase();
                      const roomB = (b.room || '').toLowerCase();
                      comparison = roomA.localeCompare(roomB);
                      break;
                      case 'purchase_date':
                        comparison = new Date(a.purchase_date) - new Date(b.purchase_date);
                        break;
                        default:
                          return 0;
                        }
                        
    if (sortOrder === 'desc') {
      comparison = -comparison;
    }
    
    return comparison;
  });
  
  // Обработчик клика на заголовок столбца
  const handleSort = (column) => {
    if (sortColumn === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortColumn(column);
      setSortOrder('asc');
    }
  };

  return (
    <Container maxWidth="lg">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom style={{ textAlign: 'center', fontSize: '30px' }}>
          <strong>История перемещений</strong>
        </Typography>

        {/* Поисковая строка */}
        <TextField
          label="Поиск"
          fullWidth
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ marginBottom: '16px' }}
        />

        {/* Таблица */}
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell 
                onClick={() => requestSort('date')}
                sx={{ cursor: 'pointer' }}
                  >
                  Дата {' '}
                  {sortColumn === 'date' && <span>(sortOrder === 'asc' ? '↑' : '↓')</span>}
                </TableCell>
                <TableCell onClick={() => requestSort('inventory_number')}>
                  Инвентарный номер {sortConfig.key === 'inventory_number' ? (sortConfig.direction === 'asc' ? '↑' : '↓') : ''}
                </TableCell>
                <TableCell onClick={() => requestSort('asset_name')}>
                  Наименование {sortConfig.key === 'asset_name' ? (sortConfig.direction === 'asc' ? '↑' : '↓') : ''}
                </TableCell>
                <TableCell onClick={() => requestSort('from_room')}>
                  Из помещения {sortConfig.key === 'from_room' ? (sortConfig.direction === 'asc' ? '↑' : '↓') : ''}
                </TableCell>
                <TableCell onClick={() => requestSort('to_room')}>
                  В помещение {sortConfig.key === 'to_room' ? (sortConfig.direction === 'asc' ? '↑' : '↓') : ''}
                </TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {sortedMoves.map((move, index) => (
                <TableRow key={index}>
                  <TableCell>{new Date(move.date).toLocaleDateString()}</TableCell>
                  <TableCell>{move.inventory_number}</TableCell>
                  <TableCell>{move.asset_name}</TableCell>
                  <TableCell>{move.from_room || '-'}</TableCell>
                  <TableCell>{move.to_room || '-'}</TableCell>
                </TableRow>
              ))}
              {sortedMoves.length === 0 && (
                <TableRow>
                  <TableCell colSpan={5} align="center">
                    Нет данных
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
    </Container>
  );
};

export default MovesList;
