import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableRow,
  Box,
} from '@mui/material';

const ChangesList = () => {
  const [changes, setChanges] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/changes')
      .then(res => setChanges(res.data))
      .catch(err => console.error('Ошибка загрузки истории:', err));
  }, []);

  return (
    <Container maxWidth="lg">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom>
          История изменений
        </Typography>

        <Table size="small">
          <TableBody>
            {changes.length > 0 ? (
              changes.map((change) => {
                // Определяем, был ли это "create" или "delete"
                const isCreate = change.field === 'created';
                const isDelete = change.field === 'deleted';

                let displayField = change.field;
                let displayOld = change.old_value || '-';
                let displayNew = change.new_value || '-';

                if (isCreate) {
                  displayField = 'Создание актива';
                  displayNew = '—';
                }

                if (isDelete) {
                  displayField = 'Удаление актива';
                  displayOld = '—';
                  displayNew = '—';
                }

                return (
                  <TableRow key={change.id}>
                    <TableCell>
                      <strong>Дата:</strong> {change.changed_at}<br />
                      <strong>Оборудование:</strong>{' '}
                      {isDelete ? `ID ${change.asset_id}` : change.full_name || change.inventory_number}<br />
                      <strong>Действие:</strong> {displayField}<br />
                      <strong>Было:</strong> {displayOld}<br />
                      <strong>Стало:</strong> {displayNew}<br />
                      <strong>Кто изменил:</strong> {change.changed_by}
                    </TableCell>
                  </TableRow>
                );
              })
            ) : (
              <TableRow>
                <TableCell align="center">Нет записей об изменениях</TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
};

export default ChangesList;
