import React, { useState, useEffect } from 'react';
import {
  Container,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Typography
} from '@mui/material';
import axios from 'axios';

const MovesList = () => {
  const [moves, setMoves] = useState([]);
  const [filteredMoves, setFilteredMoves] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortColumn, setSortColumn] = useState('date');
  const [sortOrder, setSortOrder] = useState('asc');

  // Получаем данные с сервера
  useEffect(() => {
    const fetchMoves = async () => {
      const res = await axios.get('http://localhost:5000/api/moves');
      setMoves(res.data);
    };
    fetchMoves();
  }, []);

  // Фильтрация по строке поиска
  useEffect(() => {
    if (!searchTerm.trim()) {
      setFilteredMoves(moves);
      return;
    }

    const searchLower = searchTerm.toLowerCase();

    const filtered = moves.filter(move => {
      const fieldsToSearch = [
        move.inventory_number || '',
        move.asset_name || '',
        move.from_room || '',
        move.to_room || '',
        new Date(move.date).toLocaleDateString() || ''
      ].join(' ').toLowerCase();

      return fieldsToSearch.includes(searchLower);
    });

    setFilteredMoves(filtered);
  }, [searchTerm, moves]);

  // Сортировка активов
  const sortedMoves = [...filteredMoves].sort((a, b) => {
    let comparison = 0;

    switch (sortColumn) {
      case 'date':
        comparison = new Date(a.date) - new Date(b.date);
        break;
      case 'inventory_number':
        comparison = a.inventory_number.localeCompare(
          b.inventory_number,
          undefined,
          { numeric: true }
        );
        break;
      case 'asset_name':
        comparison = a.asset_name.localeCompare(b.asset_name, undefined, { sensitivity: 'base' });
        break;
      case 'from_room':
        const roomA = (a.from_room || '').toLowerCase();
        const roomB = (b.from_room || '').toLowerCase();
        comparison = roomA.localeCompare(roomB);
        break;
      case 'to_room':
        const toRoomA = (a.to_room || '').toLowerCase();
        const toRoomB = (b.to_room || '').toLowerCase();
        comparison = toRoomA.localeCompare(toRoomB);
        break;
      default:
        return 0;
    }

    if (sortOrder === 'desc') {
      comparison = -comparison;
    }

    return comparison;
  });

  // Обработчик клика на заголовок
  const handleSort = (column) => {
    if (sortColumn === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortColumn(column);
      setSortOrder('asc');
    }
  };

  return (
    <Container maxWidth="lg">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom style={{ textAlign: 'center', fontSize: '30px' }}>
          <strong>История перемещений</strong>
        </Typography>

        {/* Поисковая строка */}
        <TextField
          label="Поиск"
          fullWidth
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ marginBottom: '16px' }}
        />

        {/* Таблица */}
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell
                  onClick={() => handleSort('date')}
                  sx={{ cursor: 'pointer' }}
                >
                  Дата {sortColumn === 'date' && (sortOrder === 'asc' ? '↑' : '↓')}
                </TableCell>
                <TableCell
                  onClick={() => handleSort('inventory_number')}
                  sx={{ cursor: 'pointer' }}
                >
                  Инвентарный номер{' '}
                  {sortColumn === 'inventory_number' && (sortOrder === 'asc' ? '↑' : '↓')}
                </TableCell>
                <TableCell
                  onClick={() => handleSort('asset_name')}
                  sx={{ cursor: 'pointer' }}
                >
                  Наименование{' '}
                  {sortColumn === 'asset_name' && (sortOrder === 'asc' ? '↑' : '↓')}
                </TableCell>
                <TableCell
                  onClick={() => handleSort('from_room')}
                  sx={{ cursor: 'pointer' }}
                >
                  Из помещения{' '}
                  {sortColumn === 'from_room' && (sortOrder === 'asc' ? '↑' : '↓')}
                </TableCell>
                <TableCell
                  onClick={() => handleSort('to_room')}
                  sx={{ cursor: 'pointer' }}
                >
                  В помещение{' '}
                  {sortColumn === 'to_room' && (sortOrder === 'asc' ? '↑' : '↓')}
                </TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {sortedMoves.map((move, index) => (
                <TableRow key={index}>
                  <TableCell>{new Date(move.date).toLocaleDateString()}</TableCell>
                  <TableCell>{move.inventory_number}</TableCell>
                  <TableCell>{move.asset_name}</TableCell>
                  <TableCell>{move.from_room || '-'}</TableCell>
                  <TableCell>{move.to_room || '-'}</TableCell>
                </TableRow>
              ))}
              {sortedMoves.length === 0 && (
                <TableRow>
                  <TableCell colSpan={5} align="center">
                    Нет данных
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
    </Container>
  );
};

export default MovesList;
