import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  IconButton,
  Tooltip,
  Box,
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

const DepartmentList = () => {
  const [departments, setDepartments] = useState([]);
  const [employeeCounts, setEmployeeCounts] = useState({});
  const [assetCounts, setAssetCounts] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Загружаем отделы
        const depsRes = await axios.get('http://localhost:5000/api/departments');
        const deps = depsRes.data;
        setDepartments(deps);

        // Для каждого отдела загружаем количество сотрудников и оборудования
        const empCounts = {};
        const assetCounts = {};

        for (let dept of deps) {
          const empRes = await axios.get(`http://localhost:5000/api/employees?department_id=${dept.id}`);
          empCounts[dept.id] = empRes.data.length;

          const assetRes = await axios.get(`http://localhost:5000/api/assets?department_id=${dept.id}`);
          assetCounts[dept.id] = assetRes.data.length;
        }

        setEmployeeCounts(empCounts);
        setAssetCounts(assetCounts);
      } catch (err) {
        console.error('Ошибка при загрузке данных:', err);
      }
    };

    fetchData();
  }, []);

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom>
          Список отделов
        </Typography>

        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Название</TableCell>
              <TableCell align="right">Сотрудников</TableCell>
              <TableCell align="right">Оборудования</TableCell>
              <TableCell align="right">Действия</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {departments.map(dept => (
              <TableRow key={dept.id}>
                <TableCell>{dept.name}</TableCell>
                <TableCell align="right">{employeeCounts[dept.id] || 0}</TableCell>
                <TableCell align="right">{assetCounts[dept.id] || 0}</TableCell>
                <TableCell align="right">
                  <Box display="flex" gap={1}>
                    <Tooltip title="Редактировать">
                      <IconButton color="primary" >
                        <EditIcon />
                      </IconButton>
                    </Tooltip>
                    <Tooltip title="Удалить">
                      <IconButton color="error">
                        <DeleteIcon />
                      </IconButton>
                    </Tooltip>
                  </Box>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
};

export default DepartmentList;
