// frontend/src/components/EmployeeList.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Box,
} from '@mui/material';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [assetCounts, setAssetCounts] = useState({}); // Количество оборудования на сотрудника

  useEffect(() => {
    const fetchData = async () => {
      try {
        const empRes = await axios.get('http://localhost:5000/api/employees');
        setEmployees(empRes.data);

        const counts = {};
        for (let emp of empRes.data) {
          const assetRes = await axios.get(`http://localhost:5000/api/assets?responsible_person=${emp.surname} ${emp.name}`);
          counts[emp.id] = assetRes.data.length;
        }

        setAssetCounts(counts);
      } catch (err) {
        console.error('Ошибка при загрузке данных:', err);
      }
    };

    fetchData();
  }, []);

  return (
    <Container maxWidth="lg">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom>
          Сотрудники
        </Typography>

        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Фамилия</TableCell>
              <TableCell>Имя</TableCell>
              <TableCell>Отчество</TableCell>
              <TableCell>Должность</TableCell>
              <TableCell>Отдел</TableCell>
              <TableCell>Помещение</TableCell>
              <TableCell>Вн. телефон</TableCell>
              <TableCell align="right">Оборудования</TableCell> {/* Новый столбец */}
            </TableRow>
          </TableHead>
          <TableBody>
            {employees.map(emp => (
              <TableRow key={emp.id}>
                <TableCell>{emp.surname}</TableCell>
                <TableCell>{emp.name}</TableCell>
                <TableCell>{emp.patronymic || '-'}</TableCell>
                <TableCell>{emp.position || '-'}</TableCell>
                <TableCell>{emp.department?.name || 'Не указано'}</TableCell>
                <TableCell>{emp.room || '-'}</TableCell>
                <TableCell>{emp.internal_phone || '-'}</TableCell>
                <TableCell align="right">{assetCounts[emp.id] || 0}</TableCell> {/* Оборудование */}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
};

export default EmployeeList;
