// frontend/src/components/AssetDetails.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  TextField,
  Button,
  Box
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react';

const AssetDetails = () => {
  const { id } = useParams(); // Получаем id из URL
  const navigate = useNavigate();
  const [asset, setAsset] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!id || id === 'new') {
      setLoading(false);
      return;
    }

    // Загружаем актив по ID
    axios.get(`http://localhost:5000/api/assets/${id}`)
      .then(res => {
        setAsset(res.data);
      })
      .catch(err => {
        console.error('Ошибка загрузки актива:', err);
        alert('Не удалось загрузить актив');
        navigate('/');
      })
      .finally(() => {
        setLoading(false);
      });
  }, [id]);

  const handleChange = (e) => {
    setAsset({ ...asset, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5000/api/assets/${id}`, asset);
      alert('Данные успешно сохранены');
    } catch (err) {
      alert('Ошибка при сохранении данных');
      console.error(err);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Вы уверены, что хотите удалить этот актив?')) {
      return;
    }

    try {
      await axios.delete(`http://localhost:5000/api/assets/${id}`);
      alert('Актив успешно удален');
      navigate('/'); // Возвращаемся к списку
    } catch (err) {
      alert('Ошибка при удалении актива');
      console.error(err);
    }
  };

  const handlePrintQRCode = () => {
    if (!asset?.inventory_number) return;

    // Создаём новый документ
    const printWindow = window.open('', '_blank', 'width=600,height=400');

    // Генерируем HTML для печати
    printWindow.document.write(`
      <!DOCTYPE html>
      <html>
        <head>
          <style>
            body {
              margin: 0;
              padding: 0;
              display: flex;
              justify-content: center;
              align-items: center;
              height: 100vh;
              background-color: #ffffff;
            }
            svg {
              max-width: 100%;
              height: auto;
            }
          </style>
        </head>
        <body>
          <svg xmlns="http://www.w3.org/2000/svg" width="200" height="200">
            <rect width="100%" height="100%" fill="#ffffff" />
            <foreignObject width="100%" height="100%">
              <div xmlns="http://www.w3.org/1999/xhtml">
                <img src="data:image/svg+xml;base64,${btoa(
                  new XMLSerializer().serializeToString(
                    document.querySelector('#qrcode').querySelector('svg')
                  )
                )}" alt="QR Code" />
              </div>
            </foreignObject>
          </svg>
        </body>
      </html>
    `);
    // Устанавливаем имя файла для сохранения
    printWindow.document.title = fileName;
    // Вызываем печать
    setTimeout(() => {
    printWindow.print();
    printWindow.close();
    }, 500);
  };

  if (loading) {
    return (
      <Container maxWidth="md">
        <Box p={3}>Загрузка...</Box>
      </Container>
    );
  }

  if (!asset) {
    return (
      <Container maxWidth="md">
        <Paper elevation={3} style={{ padding: '20px' }}>
          <Typography variant="h5" gutterBottom>Актив не найден</Typography>
          <Button variant="outlined" onClick={() => navigate(-1)}>
            Назад к списку
          </Button>
        </Paper>
      </Container>
    );
  }

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" gutterBottom>Редактировать актив: {asset.name}</Typography>

        <Grid container spacing={2}>
          <Grid item xs={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={asset.serial_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Инвентарный номер"
              name="inventory_number"
              value={asset.inventory_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Наименование"
              name="name"
              value={asset.name || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Местоположение"
              name="location"
              value={asset.location || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Ответственный"
              name="responsible_person"
              value={asset.responsible_person || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Фактический пользователь"
              name="actual_user"
              value={asset.actual_user || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Комментарии"
              name="comments"
              value={asset.comments || ''}
              onChange={handleChange}
              fullWidth
              multiline
              rows={4}
              margin="normal"
            />
            <TextField
              label="Статус"
              name="status_id"
              value={asset.status_id || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
          </Grid>
        </Grid>

        {/* QR-код */}
        <Box mt={3} display="flex" justifyContent="center">
          <div id="qrcode">
            <QRCodeSVG
              value={asset.inventory_number || 'IT-Актив'}
              size={200}
              bgColor="#ffffff"
              fgColor="#000000"
              level="H"
              onClick={handlePrintQRCode}
            />
          </div>
        </Box>

        {/* Кнопки управления */}
        <Box mt={3} display="flex" justifyContent="space-between" alignItems="center">
          <Button variant="outlined" onClick={() => navigate(-1)}>
            Назад
          </Button>

          <Box>

            <Button variant="contained" color="success" onClick={handleSave} sx={{ mr: 2 }}>
              Сохранить
            </Button>

            <Button variant="contained" color="error" onClick={handleDelete}>
              Удалить
            </Button>

          </Box>
        </Box>       
      </Paper>
    </Container>
  );
};

export default AssetDetails;
