// frontend/src/components/Directory.js

import { IconButton, Tooltip } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import React, { useEffect, useState } from 'react';
import {
  Container,
  Paper,
  Typography,
  Box,
  Table,
  TableBody,
  TableCell,
  TableRow,
  TextField,
  Button,
  Grid,
  MenuItem
} from '@mui/material';

const Directory = () => {
  const [categories, setCategories] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [employees, setEmployees] = useState([]);

  // Для добавления
  const [newCategory, setNewCategory] = useState('');
  const [newStatus, setNewStatus] = useState('');
  const [newDepartment, setNewDepartment] = useState('');
  const [newEmployee, setNewEmployee] = useState({ name: '', department_id: '' });

  const [departmentList, setDepartmentList] = useState([]);
  const [employeeDepartmentList, setEmployeeDepartmentList] = useState([]);

  const fetchAllDirectories = async () => {
    try {
      const [cats, stats, deps, emps] = await Promise.all([
        fetch('http://localhost:5000/api/categories').then(res => res.json()),
        fetch('http://localhost:5000/api/statuses').then(res => res.json()),
        fetch('http://localhost:5000/api/departments').then(res => res.json()),
        fetch('http://localhost:5000/api/employees').then(res => res.json())
      ]);

      setCategories(cats);
      setStatuses(stats);
      setDepartments(deps);
      setEmployees(emps);

      setDepartmentList([...deps]);
      setEmployeeDepartmentList([...deps]);
    } catch (err) {
      console.error('Ошибка загрузки справочников:', err);
    }
  };

  useEffect(() => {
    fetchAllDirectories();
  }, []);

  // === Категории ===
  const handleAddCategory = async () => {
    if (!newCategory.trim()) return alert('Введите категорию');
    try {
      const res = await fetch('http://localhost:5000/api/categories', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: newCategory }),
      });
      const data = await res.json();
      setCategories([...categories, data]);
      setNewCategory('');
    } catch (err) {
      alert('Ошибка при добавлении категории');
      console.error(err);
    }
  };

  const handleDeleteCategory = async (id) => {
    if (!window.confirm('Удалить эту категорию?')) return;
    try {
      await fetch(`http://localhost:5000/api/categories/${id}`, {
        method: 'DELETE'
      });
      fetchAllDirectories();
    } catch (err) {
      alert('Ошибка при удалении категории');
      console.error(err);
    }
  };

  // === Статусы ===
  const handleAddStatus = async () => {
    if (!newStatus.trim()) return alert('Введите статус');
    try {
      const res = await fetch('http://localhost:5000/api/statuses', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: newStatus }),
      });
      const data = await res.json();
      setStatuses([...statuses, data]);
      setNewStatus('');
    } catch (err) {
      alert('Ошибка при добавлении статуса');
      console.error(err);
    }
  };

  const handleDeleteStatus = async (id) => {
    if (!window.confirm('Удалить этот статус?')) return;
    try {
      await fetch(`http://localhost:5000/api/statuses/${id}`, {
        method: 'DELETE'
      });
      fetchAllDirectories();
    } catch (err) {
      alert('Ошибка при удалении статуса');
      console.error(err);
    }
  };

  // === Отделы ===
  const handleAddDepartment = async () => {
    if (!newDepartment.trim()) return alert('Введите название отдела');
    try {
      const res = await fetch('http://localhost:5000/api/departments', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: newDepartment }),
      });
      const data = await res.json();
      setDepartments([...departments, data]);
      setNewDepartment('');
    } catch (err) {
      alert('Ошибка при добавлении отдела');
      console.error(err);
    }
  };

  const handleDeleteDepartment = async (id) => {
    if (!window.confirm('Удалить этот отдел?')) return;
    try {
      await fetch(`http://localhost:5000/api/departments/${id}`, {
        method: 'DELETE'
      });
      fetchAllDirectories();
    } catch (err) {
      alert('Ошибка при удалении отдела');
      console.error(err);
    }
  };

  // === Сотрудники ===
  const handleAddEmployee = async () => {
    if (!newEmployee.name || !newEmployee.department_id) {
      return alert('Введите имя сотрудника и выберите отдел');
    }

    try {
      const res = await fetch('http://localhost:5000/api/employees', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newEmployee),
      });

      const data = await res.json();
      setEmployees([...employees, data]);
      setNewEmployee({ name: '', department_id: '' });
    } catch (err) {
      alert('Ошибка при добавлении сотрудника');
      console.error(err);
    }
  };

  const handleDeleteEmployee = async (id) => {
    if (!window.confirm('Удалить этого сотрудника?')) return;
    try {
      await fetch(`http://localhost:5000/api/employees/${id}`, {
        method: 'DELETE'
      });
      fetchAllDirectories();
    } catch (err) {
      alert('Ошибка при удалении сотрудника');
      console.error(err);
    }
  };

  return (
    <Container maxWidth="lg">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom>Справочники</Typography>

        {/* Категории */}
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <Typography variant="h6">Категории</Typography>
            <Box display="flex" gap={2} mb={2}>
              <TextField
                label="Новая категория"
                value={newCategory}
                onChange={(e) => setNewCategory(e.target.value)}
                size="small"
                fullWidth
              />
              <Button variant="contained" onClick={handleAddCategory}>
                Добавить
              </Button>
            </Box>
            <Table size="small">
              <TableBody>
                {categories.map(cat => (
                  <TableRow key={cat.id}>
                    <TableCell>{cat.name}</TableCell>
                    <TableCell align="right">
                        <Tooltip title="Удалить">
                            <IconButton color="error" onClick={() => handleDeleteCategory(cat.id)}>
                                <DeleteIcon />
                            </IconButton>
                        </Tooltip>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Grid>

          {/* Статусы */}
          <Grid item xs={6}>
            <Typography variant="h6">Статусы</Typography>
            <Box display="flex" gap={2} mb={2}>
              <TextField
                label="Новый статус"
                value={newStatus}
                onChange={(e) => setNewStatus(e.target.value)}
                size="small"
                fullWidth
              />
              <Button variant="contained" onClick={handleAddStatus}>
                Добавить
              </Button>
            </Box>
            <Table size="small">
              <TableBody>
                {statuses.map(stat => (
                  <TableRow key={stat.id}>
                    <TableCell>{stat.name}</TableCell>
                    <TableCell align="right">
                        <Tooltip title="Удалить">
                            <IconButton color="error" onClick={() => handleDeleteCategory(cat.id)}>
                                <DeleteIcon />
                            </IconButton>
                        </Tooltip>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Grid>

          {/* Отделы */}
          <Grid item xs={6} mt={4}>
            <Typography variant="h6">Отделы</Typography>
            <Box display="flex" gap={2} mb={2}>
              <TextField
                label="Новый отдел"
                value={newDepartment}
                onChange={(e) => setNewDepartment(e.target.value)}
                size="small"
                fullWidth
              />
              <Button variant="contained" onClick={handleAddDepartment}>
                Добавить
              </Button>
            </Box>
            <Table size="small">
              <TableBody>
                {departments.map(dept => (
                  <TableRow key={dept.id}>
                    <TableCell>{dept.name}</TableCell>
                    <TableCell align="right">
                      <Button size="small" color="error" onClick={() => handleDeleteDepartment(dept.id)}>
                        Удалить
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Grid>

          {/* Сотрудники */}
          <Grid item xs={6} mt={4}>
            <Typography variant="h6">Сотрудники</Typography>
            <Box display="flex" gap={2} mb={2}>
              <TextField
                label="ФИО"
                value={newEmployee.name}
                onChange={(e) => setNewEmployee({ ...newEmployee, name: e.target.value })}
                size="small"
                sx={{ flexGrow: 1 }}
              />
              <TextField
                select
                label="Отдел"
                value={newEmployee.department_id}
                onChange={(e) => setNewEmployee({ ...newEmployee, department_id: e.target.value })}
                size="small"
                sx={{ minWidth: 150 }}
              >
                {departmentList.map(d => (
                  <MenuItem key={d.id} value={d.id}>
                    {d.name}
                  </MenuItem>
                ))}
              </TextField>
              <Button variant="contained" onClick={handleAddEmployee}>
                Добавить
              </Button>
            </Box>
            <Table size="small">
              <TableBody>
                {employees.map(emp => {
                  const dept = employeeDepartmentList.find(d => d.id === emp.department_id);
                  return (
                    <TableRow key={emp.id}>
                      <TableCell>{emp.name}</TableCell>
                      <TableCell>{dept?.name || 'Не указано'}</TableCell>
                      <TableCell align="right">
                        <Button size="small" color="error" onClick={() => handleDeleteEmployee(emp.id)}>
                          Удалить
                        </Button>
                      </TableCell>
                    </TableRow>
                  );
                })}
              </TableBody>
            </Table>
          </Grid>
        </Grid>
      </Paper>
    </Container>
  );
};

export default Directory;
