// frontend/src/components/AssetDetails.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Card,
  CardContent,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Alert,
  CircularProgress,
  Grid
} from '@mui/material';
import axios from 'axios';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import DeleteIcon from '@mui/icons-material/Delete';
import PrintIcon from '@mui/icons-material/Print';

const AssetDetails = ({ currentUser }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [asset, setAsset] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const [deleteError, setDeleteError] = useState('');
  const [qrCodeUrl, setQrCodeUrl] = useState(''); // Состояние для URL QR-кода

  useEffect(() => {
    const fetchAsset = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`http://localhost:5000/api/assets/${id}`); // Замените на ваш REACT_APP_API_URL
        setAsset(response.data);
        setError('');
      } catch (err) {
        console.error('Ошибка при загрузке актива:', err);
        setError('Не удалось загрузить данные актива.');
      } finally {
        setLoading(false);
      }
    };

    fetchAsset();
  }, [id]);

  // useEffect для загрузки URL QR-кода
  useEffect(() => {
    if (asset && asset.id) { // Убедимся, что asset загружен и у него есть id
      // Формируем URL для запроса QR-кода с бэкенда
      // Замените http://localhost:5000 на ваш REACT_APP_API_URL
      const qrUrl = `http://localhost:5000/api/assets/${asset.id}/qr`;
      setQrCodeUrl(qrUrl);
    }
  }, [asset]); // Зависимость от asset

  const handleDelete = async () => {
    try {
      await axios.delete(`http://localhost:5000/api/assets/${id}`); // Замените на ваш REACT_APP_API_URL
      navigate('/assets');
    } catch (err) {
      console.error('Ошибка при удалении актива:', err);
      setDeleteError('Не удалось удалить актив.');
    }
  };

  const handleOpenDeleteDialog = () => {
    setOpenDeleteDialog(true);
  };

  const handleCloseDeleteDialog = () => {
    setOpenDeleteDialog(false);
    setDeleteError('');
  };

  const handlePrintQRCode = () => {
    if (qrCodeUrl) {
      const printWindow = window.open(qrCodeUrl, '_blank');
      printWindow.onload = () => {
        printWindow.print();
      };
    }
  };

  if (loading) return (
    <Container maxWidth="md" style={{ marginTop: '20px', textAlign: 'center' }}>
      <CircularProgress />
      <Typography>Загрузка...</Typography>
    </Container>
  );
  if (error) return (
    <Container maxWidth="md" style={{ marginTop: '20px' }}>
      <Alert severity="error">{error}</Alert>
    </Container>
  );
  if (!asset) return (
    <Container maxWidth="md" style={{ marginTop: '20px' }}>
      <Alert severity="warning">Актив не найден</Alert>
    </Container>
  );

  return (
    <Container maxWidth="md" style={{ marginTop: '20px' }}>
      <Button
        startIcon={<ArrowBackIcon />}
        onClick={() => navigate('/assets')}
        variant="outlined"
        style={{ marginBottom: '20px' }}
      >
        Назад к списку
      </Button>

      <Card>
        <CardContent>
          {/* Шапка с полным названием */}
          <Typography variant="h5" gutterBottom>
            {asset.category_name || 'Без категории'} {asset.brand} {asset.model}
          </Typography>

          <Grid container spacing={2}>
            <Grid item xs={12} md={6}>
              <Typography><strong>Инвентарный номер:</strong> {asset.inventory_number}</Typography>
              <Typography><strong>Категория:</strong> {asset.category_name || '-'}</Typography>
              <Typography><strong>Фирма:</strong> {asset.brand || '-'}</Typography>
              <Typography><strong>Модель:</strong> {asset.model || '-'}</Typography>
              <Typography><strong>Конфигурация:</strong> {asset.type || '-'}</Typography>
              {/* Добавлены новые поля */}
              <Typography><strong>Диагональ:</strong> {asset.diagonal || '-'}</Typography>
              <Typography><strong>CPU:</strong> {asset.CPU || '-'}</Typography>
              <Typography><strong>RAM:</strong> {asset.RAM || '-'}</Typography>
              <Typography><strong>Drive:</strong> {asset.Drive || '-'}</Typography>
              <Typography><strong>OS:</strong> {asset.OS || '-'}</Typography>
              <Typography><strong>IP-адрес:</strong> {asset['IP-address'] || '-'}</Typography>
              <Typography><strong>Номер:</strong> {asset.number || '-'}</Typography>
            </Grid>
            <Grid item xs={12} md={6}>
              <Typography><strong>Статус:</strong> {asset.status_id}</Typography>
              <Typography><strong>Фактический пользователь:</strong> {asset.actual_user || '-'}</Typography>
              <Typography><strong>Ответственный:</strong> {asset.responsible_person || '-'}</Typography>
              <Typography><strong>Отдел:</strong> {asset.department_id || '-'}</Typography>
              <Typography><strong>Помещение:</strong> {asset.room || '-'}</Typography>
              <Typography><strong>Дата покупки:</strong> {asset.purchase_date || '-'}</Typography>
              <Typography><strong>Комментарии:</strong> {asset.comments || '-'}</Typography>
            </Grid>
          </Grid>
        </CardContent>
      </Card>

      {/* Блок с QR-кодом */}
      <Card variant="outlined" style={{ marginTop: '20px' }}>
        <CardContent>
          <Typography variant="h6" gutterBottom>
            QR-код актива
          </Typography>
          {qrCodeUrl ? (
            <Box display="flex" flexDirection="column" alignItems="center">
              {/* Отображаем изображение QR-кода, запрашивая его у бэкенда */}
              <img
                src={qrCodeUrl}
                alt={`QR-код для актива ${asset.inventory_number}`}
                style={{ maxWidth: '200px', height: 'auto', marginBottom: '10px' }}
              />
              <Button
                variant="contained"
                color="primary"
                startIcon={<PrintIcon />}
                onClick={handlePrintQRCode}
              >
                Печать QR-кода
              </Button>
            </Box>
          ) : (
            <Typography>Загрузка QR-кода...</Typography>
          )}
        </CardContent>
      </Card>

      {/* Кнопки действий (только для администраторов) */}
      {currentUser && currentUser.role === 'admin' && (
        <Box display="flex" justifyContent="flex-end" mt={2}>
          <Button
            variant="outlined"
            color="primary"
            onClick={() => navigate(`/assets/${id}/edit`)}
            style={{ marginRight: '10px' }}
          >
            Редактировать
          </Button>
          <Button
            variant="outlined"
            color="error"
            startIcon={<DeleteIcon />}
            onClick={handleOpenDeleteDialog}
          >
            Удалить
          </Button>
        </Box>
      )}

      {/* Диалог подтверждения удаления */}
      <Dialog
        open={openDeleteDialog}
        onClose={handleCloseDeleteDialog}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{"Подтвердите удаление"}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Вы уверены, что хотите удалить актив "{asset.inventory_number}"?
            Это действие нельзя отменить.
          </DialogContentText>
          {deleteError && (
            <Alert severity="error" style={{ marginTop: '10px' }}>
              {deleteError}
            </Alert>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDeleteDialog} color="primary">
            Отмена
          </Button>
          <Button onClick={handleDelete} color="error" autoFocus>
            Удалить
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default AssetDetails;
