// frontend/src/components/AddAsset.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  TextField,
  Button,
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

const AddAsset = () => {
  const navigate = useNavigate();
  const [asset, setAsset] = useState({
    serial_number: '',
    inventory_number: '',
    name: '',
    category_id: '', // Обязательное поле
    location: '',
    responsible_person: '',
    actual_user: '',
    comments: '',
    status_id: '', // Обязательное поле
  });

  const [categories, setCategories] = useState([]);
  const [statuses, setStatuses] = useState([]);

  const [errors, setErrors] = useState({});

  // Загрузка категорий и статусов при монтировании компонента
  useEffect(() => {
    axios.get('http://localhost:5000/api/categories')
      .then(res => setCategories(res.data))
      .catch(err => console.error('Ошибка загрузки категорий:', err));

    axios.get('http://localhost:5000/api/statuses')
      .then(res => setStatuses(res.data))
      .catch(err => console.error('Ошибка загрузки статусов:', err));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAsset({ ...asset, [name]: value });
    
    // Очистка ошибок при изменении поля
    if (value.trim() !== '') {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const handleSubmit = async () => {
    // Проверка обязательных полей
    const newErrors = {};
    if (!asset.inventory_number?.trim()) newErrors.inventory_number = 'Инвентарный номер обязателен';
    if (!asset.name?.trim()) newErrors.name = 'Наименование обязательно';
    if (!asset.category_id) newErrors.category_id = 'Категория обязательна';
    if (!asset.status_id) newErrors.status_id = 'Статус обязателен';

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      alert('Пожалуйста, заполните все обязательные поля');
      return;
    }

    try {
      const res = await axios.post('http://localhost:5000/api/assets', asset);
      alert('Актив успешно добавлен');
      navigate(`/assets/${res.data.id}`);
    } catch (err) {
      alert('Ошибка при добавлении актива');
      console.error('Ошибка при создании актива:', err.response?.data || err.message);
    }
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" gutterBottom>Добавить новый актив</Typography>

        <Grid container spacing={2}>
          <Grid item xs={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={asset.serial_number}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />

            <TextField
              label="Инвентарный номер *"
              name="inventory_number"
              value={asset.inventory_number}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.inventory_number}
              helperText={errors.inventory_number}
            />

            <TextField
              label="Наименование *"
              name="name"
              value={asset.name}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.name}
              helperText={errors.name}
            />

            <TextField
              label="Местоположение"
              name="location"
              value={asset.location}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
          </Grid>

          <Grid item xs={6}>
            <FormControl fullWidth margin="normal" error={!!errors.category_id}>
              <InputLabel id="category-label">Категория *</InputLabel>
              <Select
                labelId="category-label"
                label="Категория *"
                name="category_id"
                value={asset.category_id}
                onChange={handleChange}
              >
                {categories.map(cat => (
                  <MenuItem key={cat.id} value={cat.id}>
                    {cat.name}
                  </MenuItem>
                ))}
              </Select>
              {errors.category_id && (
                <Typography color="error" variant="caption">
                  {errors.category_id}
                </Typography>
              )}
            </FormControl>

            <FormControl fullWidth margin="normal" error={!!errors.status_id}>
              <InputLabel id="status-label">Статус *</InputLabel>
              <Select
                labelId="status-label"
                label="Статус *"
                name="status_id"
                value={asset.status_id}
                onChange={handleChange}
              >
                {statuses.map(stat => (
                  <MenuItem key={stat.id} value={stat.id}>
                    {stat.name}
                  </MenuItem>
                ))}
              </Select>
              {errors.status_id && (
                <Typography color="error" variant="caption">
                  {errors.status_id}
                </Typography>
              )}
            </FormControl>

            <TextField
              label="Ответственный"
              name="responsible_person"
              value={asset.responsible_person}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />

            <TextField
              label="Фактический пользователь"
              name="actual_user"
              value={asset.actual_user}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />

            <TextField
              label="Комментарии"
              name="comments"
              value={asset.comments}
              onChange={handleChange}
              fullWidth
              multiline
              rows={4}
              margin="normal"
            />
          </Grid>
        </Grid>

        <Box mt={3} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={() => navigate(-1)}>
            Отмена
          </Button>
          <Button variant="contained" color="success" onClick={handleSubmit}>
            Создать актив
          </Button>
        </Box>
      </Paper>
    </Container>
  );
};

export default AddAsset;
