// frontend/src/App.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom'; // Импортируем Navigate
import Login from './components/Login';
import AssetList from './components/AssetList';
import Header from './components/Header'; // Шапка остаётся
import AddAsset from './components/AddAsset';
import AssetDetails from './components/AssetDetails';

function App() {
  const [currentUser, setCurrentUser] = useState(null);

  const handleLogin = (user) => {
    setCurrentUser(user);
  };

  const handleLogout = () => {
    setCurrentUser(null); // Очищаем состояние
    window.location.href = '/login'; // Перенаправляем на страницу входа
  };

  return (
    <Router>
      <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
        {/* Шапка */}
        {currentUser && <Header username={currentUser.username} onLogout={handleLogout} role={currentUser.role} />}

        {/* Основной контент — без Sidebar */}
        <div style={{
          flex: 1,
          backgroundColor: '#f9f9f9',
          padding: '20px'
        }}>
          <Routes>
            {/* Страница входа */}
            <Route path="/login" element={
              !currentUser ? (
                <Login onLogin={handleLogin} />
              ) : (
                <Navigate to="/" replace />
              )
            } />

            {/* Главная страница */}
            <Route path="/" element={<AssetList currentUser={currentUser} />} />

            {/* Детальная карточка актива */}
            <Route path="/assets/:id" element={<AssetDetails currentUser={currentUser} />} />

            {/* Добавление актива */}
            <Route path="/assets/new" element={<AddAsset />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
