// frontend/src/components/AssetDetails.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Card,
  CardContent,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Alert,
  CircularProgress,
  Grid,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormHelperText,
  Paper
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import PrintIcon from '@mui/icons-material/Print';
import axios from 'axios';
import { API_BASE_URL } from '../config'; // Импорт базового URL

const AssetDetails = ({ currentUser }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [asset, setAsset] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const [deleteError, setDeleteError] = useState('');
  const [qrCodeUrl, setQrCodeUrl] = useState(''); // URL для QR-кода с сервера

  // Состояния для редактирования
  const [isEditing, setIsEditing] = useState(false);
  const [editedAsset, setEditedAsset] = useState({});
  const [editErrors, setEditErrors] = useState({});

  // Состояния для выпадающих списков
  const [types, setTypes] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [employees, setEmployees] = useState([]);

  // Состояния для динамических полей
  const [typeProperties, setTypeProperties] = useState([]); // Свойства выбранного типа
  const [dynamicFields, setDynamicFields] = useState({}); // Значения динамических полей

  // Загрузка данных актива и справочников
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError('');
      try {
        const [assetRes, typesRes, statusesRes, departmentsRes, employeesRes] = await Promise.all([
          axios.get(`${API_BASE_URL}/api/assets/${id}`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/types`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/statuses`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/departments`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/employees`, { withCredentials: true }),
        ]);

        setAsset(assetRes.data);
        setEditedAsset(assetRes.data); // Инициализируем данные для редактирования
        setDynamicFields({
          diagonal: assetRes.data.diagonal || '',
          CPU: assetRes.data.CPU || '',
          RAM: assetRes.data.RAM || '',
          Drive: assetRes.data.Drive || '',
          OS: assetRes.data.OS || '',
          IP_address: assetRes.data['IP-address'] || '', // Используем имя атрибута из модели
          number: assetRes.data.number || '',
        });
        setTypes(typesRes.data);
        setStatuses(statusesRes.data);
        setDepartments(departmentsRes.data);
        setEmployees(employeesRes.data);

        // Формируем URL для QR-кода (новый эндпоинт на сервере)
        setQrCodeUrl(`${API_BASE_URL}/api/assets/${id}/qr`);

      } catch (err) {
        console.error('Ошибка при загрузке актива:', err);
        setError('Не удалось загрузить данные актива.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  // Загрузка свойств типа при изменении editedAsset.type_id
  useEffect(() => {
    const loadTypeProperties = async () => {
      if (editedAsset.type_id) {
        try {
          const res = await axios.get(`${API_BASE_URL}/api/types/${editedAsset.type_id}/properties`, { withCredentials: true });
          setTypeProperties(res.data);
        } catch (err) {
          console.error('Ошибка при загрузке свойств типа:', err);
          setTypeProperties([]);
        }
      } else {
        setTypeProperties([]);
      }
    };

    loadTypeProperties();
  }, [editedAsset.type_id]);

  const handleDelete = async () => {
    try {
      await axios.delete(`${API_BASE_URL}/api/assets/${id}`, { withCredentials: true });
      navigate('/assets');
    } catch (err) {
      console.error('Ошибка при удалении актива:', err);
      setDeleteError('Не удалось удалить актив.');
    }
  };

  const handleOpenDeleteDialog = () => {
    setOpenDeleteDialog(true);
  };

  const handleCloseDeleteDialog = () => {
    setOpenDeleteDialog(false);
    setDeleteError('');
  };

  const handlePrintQRCode = () => {
    if (qrCodeUrl) {
      // Открываем URL QR-кода в новом окне и вызываем печать
      const printWindow = window.open(qrCodeUrl, '_blank');
      printWindow.onload = () => {
        printWindow.print();
      };
    }
  };

  // --- Логика редактирования ---
  const handleEditToggle = () => {
    if (isEditing) {
      // Отмена редактирования - сброс к исходным данным
      setEditedAsset(asset);
      setDynamicFields({
        diagonal: asset.diagonal || '',
        CPU: asset.CPU || '',
        RAM: asset.RAM || '',
        Drive: asset.Drive || '',
        OS: asset.OS || '',
        IP_address: asset['IP-address'] || '',
        number: asset.number || '',
      });
      setEditErrors({});
    } else {
      // Начало редактирования - данные уже в editedAsset
    }
    setIsEditing(!isEditing);
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditedAsset((prev) => ({ ...prev, [name]: value }));

    // Очистка ошибок при изменении поля
    if (editErrors[name]) {
      setEditErrors((prev) => ({ ...prev, [name]: '' }));
    }
  };

  // Обработчик изменения динамических полей
  const handleDynamicFieldChange = (fieldName) => (e) => {
    const { value } = e.target;
    setDynamicFields((prev) => ({ ...prev, [fieldName]: value }));
  };

  // <<<--- НОВАЯ ФУНКЦИЯ: Обработчик изменения Ответственного при редактировании
  const handleEditResponsiblePersonChange = (e) => {
    const employeeId = e.target.value;
    handleEditChange(e); // Обновляем поле responsible_person в состоянии editedAsset

    if (employeeId) {
      // Найти выбранного сотрудника в уже загруженном списке
      const selectedEmployee = employees.find(emp => emp.id === parseInt(employeeId, 10));
      if (selectedEmployee && selectedEmployee.department_id) {
        // Автоматически устанавливаем department_id
        setEditedAsset(prevAsset => ({
          ...prevAsset,
          department_id: selectedEmployee.department_id.toString() // Убедиться, что это строка
        }));

        // Очищаем ошибку для department_id, если она была
        if (editErrors.department_id) {
          setEditErrors(prevErrors => ({ ...prevErrors, department_id: '' }));
        }

      } else {
        // Если сотрудник не найден или у него нет отдела, очищаем отдел
        setEditedAsset(prevAsset => ({
          ...prevAsset,
          department_id: ''
        }));
      }
    } else {
      // Если сотрудник не выбран, очищаем отдел
      setEditedAsset(prevAsset => ({
        ...prevAsset,
        department_id: ''
      }));
    }
  };


  const handleSave = async () => {
    // Простая валидация на фронтенде (можно расширить)
    const newErrors = {};
    if (!editedAsset.inventory_number) newErrors.inventory_number = 'Обязательное поле';
    if (!editedAsset.type_id) newErrors.type_id = 'Обязательное поле';
    if (!editedAsset.status_id) newErrors.status_id = 'Обязательное поле';
    if (!editedAsset.responsible_person) newErrors.responsible_person = 'Обязательное поле';
    // department_id и purchase_date сделаем обязательными, как в AddAsset
    if (!editedAsset.department_id) newErrors.department_id = 'Обязательное поле';
    if (!editedAsset.purchase_date) newErrors.purchase_date = 'Обязательное поле';

    if (Object.keys(newErrors).length > 0) {
      setEditErrors(newErrors);
      return;
    }

    try {
      // Формируем данные для отправки, объединяя основные поля и динамические
      const dataToSend = {
        ...editedAsset,
        ...dynamicFields // Добавляем значения динамических полей
        // Убедитесь, что имена полей в dynamicFields совпадают с ожидаемыми бэкендом (diagonal, CPU и т.д.)
      };

      // Обработка IP-адреса: если ключ в dynamicFields - 'IP_address', меняем его на 'IP-address' для бэкенда
      // (Это зависит от того, как ваш бэкенд ожидает получить имя поля)
      // const finalDataToSend = { ...dataToSend };
      // if (finalDataToSend.IP_address !== undefined) {
      //    finalDataToSend['IP-address'] = finalDataToSend.IP_address;
      //    delete finalDataToSend.IP_address;
      // }

      const response = await axios.put(`${API_BASE_URL}/api/assets/${id}`, dataToSend, { withCredentials: true });
      setAsset(response.data); // Обновляем основной объект актива
      setIsEditing(false);
      setEditErrors({});
      alert('Актив успешно обновлён');
    } catch (err) {
      console.error('Ошибка при обновлении актива:', err);
      alert('Ошибка при обновлении актива: ' + (err.response?.data?.error || err.message));
    }
  };

  if (loading) return (
    <Container maxWidth="md" style={{ marginTop: '20px', textAlign: 'center' }}>
      <CircularProgress />
      <Typography>Загрузка...</Typography>
    </Container>
  );
  if (error) return (
    <Container maxWidth="md" style={{ marginTop: '20px' }}>
      <Alert severity="error">{error}</Alert>
    </Container>
  );
  if (!asset) return (
    <Container maxWidth="md" style={{ marginTop: '20px' }}>
      <Alert severity="warning">Актив не найден</Alert>
    </Container>
  );

  return (
    <Container maxWidth="lg" style={{ marginTop: '20px' }}>
      <Button
        startIcon={<ArrowBackIcon />}
        onClick={() => navigate('/assets')}
        variant="outlined"
        style={{ marginBottom: '20px' }}
      >
        Назад к списку
      </Button>

      <Card>
        <CardContent>
          {/* Шапка с полным названием */}
          <Typography >
            {asset.category_name} {asset.brand} {asset.model}
          </Typography>
            <Grid container spacing={2}>
              {/* Серийный номер */}
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Серийный номер"
                  name="serial_number"
                  value={editedAsset.serial_number || ''}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                  sx={{ mb: -1 }}
                />
              </Grid>

              {/* Инвентарный номер */}
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Инвентарный номер *"
                  name="inventory_number"
                  value={editedAsset.inventory_number || ''}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                  error={!!editErrors.inventory_number}
                  helperText={editErrors.inventory_number}
                />
              </Grid>

              {/* Категория (Тип устройства) */}
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth margin="normal" error={!!editErrors.type_id}>
                  <InputLabel id="edit-type-label">Категория *</InputLabel>
                  <Select
                    labelId="edit-type-label"
                    label="Категория *"
                    name="type_id"
                    value={editedAsset.type_id || ''}
                    onChange={handleEditChange}
                  >
                    {types.map(cat => (
                      <MenuItem key={cat.id} value={cat.id}>
                        {cat.name}
                      </MenuItem>
                    ))}
                  </Select>
                  {editErrors.type_id && (
                    <FormHelperText>{editErrors.type_id}</FormHelperText>
                  )}
                </FormControl>
              </Grid>

              {/* Фирма */}
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Фирма"
                  name="brand"
                  value={editedAsset.brand || ''}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                />
              </Grid>

              {/* Модель */}
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Модель"
                  name="model"
                  value={editedAsset.model || ''}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                />
              </Grid>

              {/* Конфигурация */}
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Конфигурация (Процессор/DDR/HDD)"
                  name="type"
                  value={editedAsset.type || ''}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                />
              </Grid>

              {/* Динамические поля, полученные из type_properties */}
              {typeProperties.map((property) => {
                const fieldName = mapPropertyNameToAssetField(property.name);
                const value = dynamicFields[fieldName] || '';
                return (
                  <Grid item xs={12} sm={6} key={property.id}>
                    <TextField
                      label={property.name}
                      name={fieldName}
                      value={value}
                      onChange={handleDynamicFieldChange(fieldName)}
                      fullWidth
                      margin="normal"
                    />
                  </Grid>
                );
              })}

              {/* Статус */}
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth margin="normal" error={!!editErrors.status_id}>
                  <InputLabel id="edit-status-label">Статус *</InputLabel>
                  <Select
                    labelId="edit-status-label"
                    label="Статус *"
                    name="status_id"
                    value={editedAsset.status_id || ''}
                    onChange={handleEditChange}
                  >
                    {statuses.map(stat => (
                      <MenuItem key={stat.id} value={stat.id}>
                        {stat.name}
                      </MenuItem>
                    ))}
                  </Select>
                  {editErrors.status_id && (
                    <FormHelperText>{editErrors.status_id}</FormHelperText>
                  )}
                </FormControl>
              </Grid>

              {/* Фактический пользователь */}
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Фактический пользователь"
                  name="actual_user"
                  value={editedAsset.actual_user || ''}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                />
              </Grid>

              {/* Ответственный */}
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth margin="normal" error={!!editErrors.responsible_person}>
                  <InputLabel id="edit-responsible-person-label">Ответственный *</InputLabel>
                  <Select
                    labelId="edit-responsible-person-label"
                    label="Ответственный *"
                    name="responsible_person"
                    value={editedAsset.responsible_person || ''}
                    // <<<--- ИЗМЕНЕНО: Используем новую функцию
                    onChange={handleEditResponsiblePersonChange}
                  >
                    {employees.map(emp => (
                      <MenuItem key={emp.id} value={emp.id}>
                        {emp.name}
                      </MenuItem>
                    ))}
                  </Select>
                  {editErrors.responsible_person && (
                    <FormHelperText>{editErrors.responsible_person}</FormHelperText>
                  )}
                </FormControl>
              </Grid>

              {/* Отдел (подразделение) */}
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth margin="normal" error={!!editErrors.department_id}>
                  <InputLabel id="edit-department-label">Отдел (подразделение) *</InputLabel>
                  <Select
                    labelId="edit-department-label"
                    label="Отдел (подразделение) *"
                    name="department_id"
                    value={editedAsset.department_id || ''}
                    onChange={handleEditChange}
                    // <<<--- ИЗМЕНЕНО: Поле заблокировано для редактирования
                    disabled
                    style={{
                      backgroundColor: '#f5f5f5',
                      color: 'rgba(0, 0, 0, 0.87)',
                      cursor: 'not-allowed'
                    }}
                  >
                    {departments.map(dept => (
                      <MenuItem key={dept.id} value={dept.id}>
                        {dept.name}
                      </MenuItem>
                    ))}
                  </Select>
                  {editErrors.department_id && (
                    <FormHelperText>{editErrors.department_id}</FormHelperText>
                  )}
                </FormControl>
              </Grid>

              {/* Помещение */}
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Помещение"
                  name="room"
                  value={editedAsset.room || ''}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                />
              </Grid>

              {/* Дата покупки */}
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Дата покупки *"
                  name="purchase_date"
                  type="date"
                  value={editedAsset.purchase_date || ''}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                  error={!!editErrors.purchase_date}
                  helperText={editErrors.purchase_date}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </Grid>

              {/* Комментарий */}
              <Grid item xs={12}>
                <TextField
                  label="Комментарий"
                  name="comments"
                  value={editedAsset.comments || ''}
                  onChange={handleEditChange}
                  fullWidth
                  multiline
                  rows={4}
                  margin="normal"
                />
              </Grid>
            </Grid>
          )}
        </CardContent>
      </Card>

      {/* Блок с QR-кодом */}
      <Card variant="outlined" style={{ marginTop: '20px' }}>
        <CardContent>
          <Typography variant="h6" gutterBottom>
            QR-код актива
          </Typography>
          {qrCodeUrl ? (
            <Box display="flex" flexDirection="column" alignItems="center">
              {/* Отображаем изображение QR-кода, запрашивая его у сервера */}
              <img
                src={qrCodeUrl}
                alt={`QR-код для актива ${asset.inventory_number}`}
                style={{ maxWidth: '200px', height: 'auto', marginBottom: '10px' }}
              />
              <Button
                variant="contained"
                color="primary"
                startIcon={<PrintIcon />}
                onClick={handlePrintQRCode}
              >
                Печать QR-кода
              </Button>
            </Box>
          ) : (
            <Typography>Загрузка QR-кода...</Typography>
          )}
        </CardContent>
      </Card>

      {/* Кнопки действий (только для администраторов) */}
      {currentUser && currentUser.role === 'admin' && (
        <Box display="flex" justifyContent="flex-end" mt={2} gap={1}>
          {!isEditing ? (
            <>
              <Button
                variant="outlined"
                color="primary"
                startIcon={<EditIcon />}
                onClick={handleEditToggle}
              >
                Редактировать
              </Button>
              <Button
                variant="outlined"
                color="error"
                startIcon={<DeleteIcon />}
                onClick={handleOpenDeleteDialog}
              >
                Удалить
              </Button>
            </>
          ) : (
            <>
              <Button
                variant="outlined"
                color="secondary"
                onClick={handleEditToggle}
              >
                Отмена
              </Button>
              <Button
                variant="contained"
                color="primary"
                onClick={handleSave}
              >
                Сохранить
              </Button>
            </>
          )}
        </Box>
      )}

      {/* Диалог подтверждения удаления */}
      <Dialog
        open={openDeleteDialog}
        onClose={handleCloseDeleteDialog}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{"Подтвердите удаление"}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Вы уверены, что хотите удалить актив "{asset.inventory_number}"?
            Это действие нельзя отменить.
          </DialogContentText>
          {deleteError && (
            <Alert severity="error" style={{ marginTop: '10px' }}>
              {deleteError}
            </Alert>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDeleteDialog} color="primary">
            Отмена
          </Button>
          <Button onClick={handleDelete} color="error" autoFocus>
            Удалить
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

// Функция сопоставления имени свойства с полем в Asset (дублируется из AddAsset для согласованности)
const mapPropertyNameToAssetField = (propertyName) => {
  const mapping = {
    'Диагональ': 'diagonal',
    'Диагональ (дюймы)': 'diagonal',
    'Операционная система': 'OS',
    'Процессор': 'CPU',
    'Оперативная память (ГБ)': 'RAM',
    'Оперативная память': 'RAM',
    'Диск (HDD/SSD)': 'Drive',
    'Диск': 'Drive',
    'IP-адрес': 'IP_address', // В JS удобнее работать с IP_address
    'Внутренний номер': 'number',
  };
  return mapping[propertyName] || propertyName.toLowerCase().replace(/\s+/g, '_'); // fallback
};

export default AssetDetails;
