// frontend/src/App.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import AssetList from './components/AssetList';
import AssetDetails from './components/AssetDetails';
import Directory from './components/Directory';
import EmployeeList from './components/EmployeeList';

export const NavigationContext = React.createContext();

function App() {
  const [selectedAssets, setSelectedAssets] = useState([]);

  const navigateWithConfirm = (navigate, path) => {
    if (selectedAssets.length > 0) {
      const userConfirmed = window.confirm(
        'Снять выделение и перейти на другую страницу?'
      );
      if (!userConfirmed) return; // Переход заблокирован
      setSelectedAssets([]); // Снимаем выделение
    }
    navigate(path);
  };

  return (
    <NavigationContext.Provider value={{ selectedAssets, setSelectedAssets, navigateWithConfirm }}>
      <Router>
        <div style={{ display: 'flex' }}>
          <Sidebar />
          <div style={{ flex: 1, padding: '20px' }}>
            <Routes>
              <Route path="/assets" element={<AssetList />} />
              <Route path="/assets/:id" element={<AssetDetails />} />
              <Route path="/employees" element={<EmployeeList />} />
              <Route path="/directory" element={<Directory />} />
            </Routes>
          </div>
        </div>
      </Router>
    </NavigationContext.Provider>
  );
}

export default App;
